INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115217,"Balayia kapa Kurinci","Balabuah tantang pulau Panai","Kok banci katokan banci","Jangan ditulak jo parangai", 4, 6);
