INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115219,"Balayia kapa Siantan","Balabuah tantang Indogiri","Layiakan sayo dalam badan","Tolong dayuangkan dalam ati", 4, 6);
