INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115221,"Balayia pelang rang Sinuruik","Sarek bamuek batang padi","Patuiklah adiak ganjua suruik","Lah nampak suko misikin kami", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115222,"Berlayar pelang orang Sinuruik","Sarat bermuatan batang padi","Patutlah adik melangkah surut","Telah tampak sukar dan miskin kami", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115223,"Balayia sambilan bulan","Mati tukang mati kalasi","Dikandakan aia dalam bulan","Bulan dilingka nago sati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115224,"Berlayar sembilan bulan","Mati tukang mati kelasi","Menginginkan air dalam bulan","Bulan dilingkar naga sakti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115225,"Balayia tantang Taluak Kasai","Sudah bamuek daun padi","Aia mato salamo carai","Kok sumua elok tampek mandi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115226,"Bale bedek terpiaq leman papan","Dasar tiangne tejanggel isiq batu","Lamunku jaoq eleq desadengan","Endeqku lupaq gumi paerku", 4, 6);
