INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115229,"Baledang di Rantau Panjang","Ditikam kanai radaiknyo","Jikalau umua samo panjang","Di baleh juo guno baiknyo", 4, 6);
