INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115766,"Banyaklah ubi dikebun sebelah","Ubi dikerat dibuang bukunya","Banyaklah Nabi diturunkan Allah","Nabi Muhammad jadi penghulunya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115767,"Banyaklah ubi perkara ubi","Ubi merambat tepi-tepian","Banyaklah nabi perkara nabi","Nabi Muhammad nabi pilihan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115768,"Banyaklah udang masuk tengkalak","Pagi petang orang menunggu","Hendak kupinang takut ditolak","Matilah abang menahan rindu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115769,"Banyaklah udang sumbarang udang","Udang dibawok rang Kayutanam","Banyaklah urang sumbarang urang","Adapun urang tabagi anam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115770,"Banyaklah ungka perkara ungka","Besarlah ungka nak dari biawak","Banyaklah durhaka perkara durhaka","Besarlah durhaka ke ibu bapak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115771,"Banyaklah upih dicari orang","Untuk pembungkus lempuk durian","Hendak kupilih kekasih orang","Mabuklah hamba duduk sendirian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115772,"Banyaknya pokok ditebang","Si badang kononya kebal","Dipuji hidungnya kembang","Dimarah telinganya tebal", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115773,"Banyaknya pulau di negeri Sabah","Negeri bergelar di bawah bayu","Jika terpulau hendaklah tabah","Jika terselar janganlah layu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115774,"Banyaknya sukun tidak terbawa","Habis dulang dibeli orang","Pandai berlakon di depan ketua","Mencuri tulang pekerja cemerlang?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115775,"Banyur setandan keluarkan panggil","Kumbang bercampur tara manis","Cium tinggal pelukpun tinggal","Kakahanda pulang jangan menangis", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115776,"Bao lenggang den di bulan","Isuak bao limbua pulo","Bawa Hang den di tuan","Isuak bao timbua pulo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115777,"Bawa melenggang saya oleh bulan","Esok dibawa limbur pula","Bawa hilang saya oleh tuan","Esok dibawa timbul pulo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115778,"Bapa gergasi menebar jala","Pegang tali melentok-lentok","Masakan pengerusi tak garu kepala","Kalau dah ahli semua mengantuk", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115779,"Bapa gergasi menebar jala","Pegang tali melentuk-lentuk","Masakan pengerusi ta' garu kepala","Kalau dah ahli semua mengantuk.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115780,"Bapa Saonang tinggal di Jati","Buah kenari di Kampung Orpah","Rasanya senang dalam hati","Mari kita mengangkat sumpah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115781,"Bapadati muatan sarek","Bangkahulu sasudah Tapan","Pacik pitaruah buhua arek","Umanaik jan dilupokan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115782,"Bapak ayam jantan","Dudok makan padi","Oghang suroh ligan","Takot cakaq padi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115783,"Bapak dengan ibu","Dudok tak dak kerja","Musim bulan Satu","Timoq cukup tua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115784,"Bapak dunia bagilo-gilo","Ibunyo suko babanyak-banyak","Sudaronyo jahia sadonyo","Suaminyo bohong jo bongak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115785,"Bapak gajah jatoh gaung","Gaung luaih Bukit Berapit","Oghang megah tak usah tulong","Biaq dia rasa sakit", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115786,"Bapak leteh kerja","Bangun tengah hari","Banyak buat taik mata","Malam main judi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115787,"Bapak leteh kerja","Lambat bangun pagi","Banyak buat taik mata","Malam main judi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115788,"Bapak nyekena nelpun","Ineq nyekena masak","Ta berpisah araq setahun","Lagu side uwah bedowe anak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115789,"Bapakai adat batiru batuladan","Makai hereng dengan gendeang","Himbau nan biaso basahuti","Adat nan samo bapakaian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115790,"Baparak ka tangah koto","Tampek batanam sulasiah Jambi","Kalau tak banyak kitab dibaco","Indak lah sah manjadi Kadi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115791,"Bapilih tareh dipijakkan","Manggapai-gapai tak baranjak","Masin jo padeh pabauakan","Di sinan gulai mangkonyo lamak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115792,"Bapucuak bulek baurek tunggang","Langgam nan tujuah alah tasusun","Panghulu di Lareh Koto Piliang","Bajanjang naiak batanggo turun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115793,"Bapupuik di gulang-gulang","Dapeklah bondo dek bapikek","Tasabuik adat rang Minang","Pandai mambaco nan tasirek", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115794,"Bara api di-dalam puan","Kemiyau ada di-dalam chelpa","Buah hati jantong-ku tuan","Ingat-lah tuan jangan-lah lupa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115795,"Bara api jangan diinjak","Panas melepuh terasa kaki","Terima nasihat orang bijak","Walau tidak disenangi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115796,"Barabab batali tujuah","Bagandang lalu ka tapian","Sabab mumbang tidak tumbuah","Dirandang paneh tujuah bulan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115797,"Bermain rebab bertali tujuh","Bergendang lalu ke tepian","Sebabnya mumbang tidak tumbuh","Direndang papas tujuh bulan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115798,"Barabah mudiek-an Tiku","Cumarak ateh pamatang","Ba-sabab ajuang tak lalu","Muaro di-lingka karang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115799,"Ba-rabuik rabuik ka pulau","Ka pulau man-cari udang","Kok iduik lawan ba-gurau","Kok mati ta-bariang surang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115800,"Barado panjang alaman","Sadapo sapuluah jari","Tidak lamo sayo bajalan","Sabulan sapuluah hari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115801,"Berapa panjang halaman","Sedepa sepuluh jari","Tidak lama saya berjalan","Sebulan sepuluh hari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115802,"Barakat batang kaladan","Pauah bagolek masuak padi","Kilek tampak guruah tingaran","Apo sabab ujan tak jadi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115803,"Barakik-rakik ka hulu","Baranang-ranang ka tapian","Basakik-sakik dahulu","Basanang-sanang kamudian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115804,"Barakik-rakik ka hulu","Baranang-ranang ka tapian","Basakik-sakik dahulu","Basanang-sanang kamudian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115805,"Baralek urang di Piaman","Talayang bungkuih ragi duo","Lah rasah buruang ganggo aman","Ditapo buruang jolong tibo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115806,"Berhelat orang di Pariaman","Terlayang bungkus beragi dua","Lah resah burung ganggo aman","Dipukul burung baru tiba", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115807,"Barambang jauh ke hulu","Di kualanya kayu perapat","Berpesan kami dahulu","Akan ke sana inda sempat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115808,"Barambang saok buli-buli","Badarai tampuak lado sulah","Buruang tabang maeda bumi","Inggok di tirai Rasullillah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115809,"Berembang tutup buli-buli","Berderai tampuk lada sulah","Burung terbang mengedari bumi","Hinggap di tirai Rasullullah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115810,"Bar-ambuih timua jo utaro","Bagindo Akin rang gala-kan","Handalanga di gudang kopi","Pa-laiar-an urang Tranggano", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115811,"Ba-rambuik mangko ba-gonjong","Kudian mangko ba-deta","Di-mabuak kato ta-sorong","Kudian mangko ma-nyasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115812,"Barampek tidua di parateh","Cukuik balimo jo gurunyo","Kami diganggam-ganggam lapeh","Tidak bak itu daulunyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115813,"Berempat tidur di peratas","Cukup berlima dengan gurunya","Kami digenggam-genggam lepas","Tidak begitu dahulunya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115814,"Baranang itiak di nan rawang","Batabek ikan di nan gauang","Kononlah bujang mangapalang","Di ma tagak kumari tangguang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115815,"Baranang ka hulu aie","Jan lupo jo nantieng lapuak","Alah sanang hiduik di dunie","Kana juo kamati isuak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115816,"Barang baru mau dibeli","Buah ranum di atas pinggan","Mending tak tahu sama sekali","Daripada tahu hanya setengah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115817,"Barang berat boleh diangkat","Tugas harian menjadi beban","Kemauan baik dan kuat","Semuanya bisa dikerjakan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115818,"Barang datai di pengkalan diri","Udah bebantai lalu betiki.","Terabai sangkoh sama dibai","Manjong sereta sama bebai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115819,"Barang di-dalam mana letakan","Kain baju bahru berlipat","Kita mengidam orang matakan","Belom tentu siapa mendapat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115820,"Barang dipansa nitih magang","Bekayuh sida penegar tulang","Berumpak enggau Raja nadai menang","Samoa abis tinggal ka belakang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115821,"Barang dipansa sama berumpak","Bisi lembau abis diansak.","Orang ke diluan ngangau nyeradak","Ngungkat kayuh lundai sarintak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115822,"Barang disambut bersukacita","Serta dengan berebut-rebutan","Melihat beruk pegang senjata","Kera lah mati ketakutan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115823,"Barang disambut bersukacita","Serta dengan berebut-rebutan","Melihat beruk pegang senjata","Kera'lah mati ketakutan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115824,"Barang jualan di dalam perahu","Jualan dibawa ke tanah seberang","Sebarang kerja diambil tahu","Jangan ada orang temberang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115825,"Barang jualan tepi pangkalan","Di malam purnama dekat kuala","Ada kenalan jumpa di jalan","Lama-lama tak perasan pula", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115826,"Barang ka mano ka dituruik","Mintaklah ijin ka suami","Ikuik katonyo nama nan patuik","Apa kahandaknyo handaklah bari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115827,"Barang makanan dibawa ke rumah","Barang jualan di dalam perahu","Hapuskan rasa malas dan lemah","Sebarang kerja diambil tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115828,"Barang rumah orang ke mai pala","Ditinggal di tengah laman ngetu enda lama","Kelakuyang dientak ka dunga-dunga","Pala diengkah tengah laman nya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115829,"Barang siapa berpadi emping","Padi emping huma di-tengah","Barang siapa berhati mu'min","Hati yang mu'min istana Allah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115830,"Barang siapa melawan suami","Terlalu sakit rasanya hati","Mukanya hitam seperti babi","Di dalam neraka tempat berhenti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115831,"Barang talie saya talieken","Ayer sekarang madieken roesa","Orang berdjandji saya djanjiken","Nonna sekarang tieda koe loepa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115832,"Barang yang berat dibawa turun","Itu tandanya barang berharga","Kerja yang berat janganlah gerun","Kerja selesai hati pun lega", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115833,"Barapo panjang pangalan","Buliah kami rantangi tali","Barapo lamo tuan baj alan","Buliah kami bilangkan ari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115834,"Bar-arak awan bak kini","Tingga di jalan sa-pa-duo","Tar-agak dagang bak kini","Musim pa-bilo ka sampai-nyo.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115835,"Baras pulut di dalam talam","Handak maolah wadai basamuk","Angin batiup bakirim salam","Di dalam mimpik kita batamuk", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115836,"Barat Daya kencang semalam","Singgah masuk kapal berlabuh","Terpedaya bujang semalam","Tongkat patah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115837,"Barat daya kencang semalam","Singgah masuk kapal berlabuh","Terperdaya bujang semalam","Tongkat patah pantaran roboh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115838,"Barat Daya kencang semalam","Tongkat patah titian roboh","Apalah daya bujang semalam","Mengembang bunga di wajahnya subuh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115839,"Barau riuh bersiul berkicau","Mematuk ciku di tepi kali","Suaranya garau bagai barau","Menyanyi soprano bagus sekali!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115840,"Barau-barau disebut rawa","Burung rawa membelah angkasa","Runtuh moral runtuh jiwa","Runtuh sama tamadun bangsa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115841,"Bareng semeton lalo begoncengan","Eleq langan gitaq manuk mate","Timaq kakak leq desan dengan","Laguq kakak tetep lwq ate", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115842,"Bareng-bareng lekak ndendeq bepisah","Mulai lekan bale tipaq segara","Pemerintah seneng tidak pernah susah","Lalu pesiar sugul tame Negara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115843,"Bareng-bareng te pede ngebut","Bereng-bareng te pede teme ruamah sakit","Sebut aranku agen ndaq meq pada ribut","Jemaq ngelemaq te pede bedait", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115844,"Bari ba-karih malah bulan","Bari ba-ladieng malah bintang","Bijak jauari bana tolan","Ma-andok tampek nan tarang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115845,"Bari batali kumbang Padang","Bari batali banang suto","Santuni kami anak dagang","Ka tambah tuan bumi putoro", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115846,"Bari batalilah kapuran","Bari batali jo suaso","Bari bajanji den dek tuan","Mananti adiak babicaro", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115847,"Beri bertalilah kapuran","Beri bertali dengan suasa","Beri berjanji saya oleh tuan","Menanti adik berbicara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115848,"Barih baukua jo papatah","Iyolah barih nan baanggo","Balabeh baukua jo patitih","Iyolah balabeh nan batanggo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115849,"Barimbo-rimbolah kau pandan","Karano capo babuah","Baibo-ibolah kau badan","Karano kato sabuah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115850,"Baringgin di Pusar Ulak","Buah nangka laut istana","Hati ingin belumkan tolak","Pakal saja kena bencana", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115851,"Baringin di kampuang Gasan","Pautan ayam kuniang kaki","Salamo ajo bajalan","Aia minum raso duri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115852,"Beringin di kampung Gasan","Pautan ayam kuning kaki","Selama abang berjalan","Air minum rasa duri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115853,"Baringin di kampuang Gasan","Pautan ayam taduang gombak","Ingin ati di manggih utan","Sampan ado pangayuah tidak", 4, 6);
