INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115875,"Baru diketam setangkai padi","Padi hampa dimakan serangga","Sejarah hitam cinta abadi","Malang suami main kayu tiga", 4, 6);
