INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (115884,"Baru sedepa atap di bumbung","Batu-bata belum diangkat","Tuan papa berlagak sombong","Bagai si buta membuang tongkat", 4, 6);
