INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116215,"Batu ya'kut cincin tersangga","Di pakai pangeran berbaju jingga","Musibah datang tiada terduga","Dengan sabar Iman terjaga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116216,"Batu zamrut mudah digosok","Dipakai indah ada yang noleh","Bersiap bekal 'tuk hari esok","Dengan kebaikan dan amal saleh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116217,"Batu zamrut mudah digosok","Dipakai indah ada yang noleh","Bersiap bekal 'tuk hari esok","Dengan kebaikan dan amal saleh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116218,"Batuang ba-buah tapi aia","Salaro tumbang anyolai","Ajuang ba-tuah nak ba-laia","Kualo langang anyolai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116219,"Batuang di mano ka ditabang","Batuang nan condong ka tajujan","Ajuang nan mano ka ditompang","Ajuang nan jauah palayaran", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116220,"Batu-batu saq araq kuman","Asalne leman dalem sumur","Pacu-pacu saq baca Qur'an","Jari safaat eleq dalem kubur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116221,"Batudung terendak Bantan","Pagar siapa disusurkan","Sudah untung permintaan badan","Pada siapa diserahkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116222,"Batulak poghau daghi Indagighi","Balabuo baghonti di Tambilahan","Istinjak buondok manyucikan dighi","Pangkal kabarosiohan jo kasehatan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116223,"Baturang banang baturak","Batulih kain baragi suto","Aib urang usah dibukak","Aib diri elok pareso", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116224,"Batusangka balantai batu","Parak Jua labuah basilang","Sadang jo kapa lai tak lalu","Konon barakik batang pisang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116225,"Batutua bapasiah lidah","Bakato baandia-andai","Kalau kaji alun basudah","Raso ka cangguang dek mamakai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116226,"Bau baling araq lima","Bau botor araq sebakaq","Lamun mele tame surge","Dendeq durhake leq dengan toaq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116227,"Bau baling araq sepulu","Bau kangkung araq sepesel","Lamun berajah endeq te pacu","Wah toaq aboq te nyesel", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116228,"Bau buaq nanke leq julun bale","Bauq nangke sino gen terjual jok Sepapan","Nani sere lueq kerusuhan leq embe-embe","Sementare pemerintah momot meco eleq penindoan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116229,"Bau delima masak s'tangkay","Gugor tengari ber-tabor taboran","Tuan s'pama gunong meligay","Tempat Putri Dewa Kayangan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116230,"Bau duren leq serin telage","Leq bawaq lolon duren araq daun pandan","Ndaraq gunanta jari pemimpin negare","Lamunta endeq tao pinaq negare jari aman", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116231,"Bau harum si mayang pisang","Harum dia dibawa angin","Bagei jarum dengan benang","Lama-lama penjahit kain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116232,"Bau jagung tengaq tembako","Terus tunuq kadu api","Lamun side pade meranto","Ndak lupaq anak jari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116233,"Bau kayuq aning gawah","Bau paoq siq uwah taoq","Pacu-pacu pade sekolah","Jari sango uwah ke toaq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116234,"Bau kedit leq tengaq bangket","Bedait ular siq beleq gati","Lamun side wah taeq pangket","Dendea lupaq semeton jari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116235,"Bau kenyamen araq enem","Kenyamen araq enem","Ndaqna pacu mangan nginen","Sengaq tabilin siq dengan taogna", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116236,"Bau lembain leq telabah","Beli tereng leq Peken Praya","Pacu-pacu gaweq ibadah","Jari tebeng api neraka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116237,"Bau maneq jari sedeq tampahan","Te nampah ngeang ladek sik bekarat","Ndek seselang pade perpisahan","Senga' wah te tulis lek dalem kodrat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116238,"Bau nangke jari tekelaq","Nangke tekelaq kadu panic","Para pejabat girang na lekaq","Mina' masyarakat jari benci", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116239,"Bau nyale ojoq segara Kaliantan","Endaq lupaq jauq penyoroq","Rakyat Indonesia lueq kelaparan","Sengaq para pemimpinna pada mikirang diriq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116240,"Bau nyiur le' kebon daya","Sa' jelapan teruna bajang","Munta girang lalo' begaya","Lupaq waktu pirang sembahyang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116241,"Bau nyiur nyiur bride","Sengak te kaken maiq rasane","Berukku bedait kance side","Ate angen ancur idapne", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116242,"Bau paku bilang jelo","Dendeq tepinaq lekan kayuq manis","Lamun aku bilin side lalo","Dendeq jangke pinaq side nangis", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116243,"Bau paku elek-elek","Jari kandoq sedek pepait","Lamun aku ndeq salak badek","Enggak nengke te bedait", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116244,"Bau paku leq Embung Tiang","Bau lekoq leq sedi kokoq","Lamun lalo bilin telang","Dendeq adiq aseq laloq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116245,"Bau paku leq kebon karet","Beli buku eleq kios","Lamune mele idup terhormat","Jagaq-jagaq siq ngeraos", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116246,"Bau paku leq sedin telage","Bau empak leq kokoq","Lamun sekolah ndekne uwah tama","Dendeq hareq idup leq Lomboq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116247,"Bau paku tengak telabah","Blide jari kayu","Aranku amak sidak","Namun side sai juluk", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116248,"Bau paku tengak telabah","Blide jari kayu","Aranku amak sidak","Namun side sai juluk", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116249,"Bau paoq leq julun bale","Bau nyele leq kaliantan","Timaqna jaoq ndeqta lale","Pade batur ta saling peta entan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116250,"Bau paoq masih beciq","Begang tama aning loang","Dendeq meraiq masih beciq","Laun mele bekedeq doang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116251,"Bau paoq piyaq rujak","Sedang nanas kance sebiye","Wakil rakyat bueq pade lekak","Sengaq rakyatne ndarak berue", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116252,"Bau paoq siq wah taoq","Lolona araq leq kebon loq Saleh","Pasti demen selapuq dengan toaq","Bedua anak siq alim soleh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116253,"Bau paoq siq wah toaq","Lolona araq leq kebon loq Saleh","Pasti demen selapuq dengan toaq","Bedue anak siq aim solah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116254,"Bau priye siq resene peit","Te jauq aning taman","Beruq nengke sine te bedait","Ndek neneq seleqne tebesalaman", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116255,"Bau puntiq sedin telage","Kanak dedare kadu tangkong","Lamun side ojok Jawa","Endeq lupaq tiang eleq Selong", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116256,"Bau srikaya leq tengak gawah","Tepaleq isiq mamiq Jannah","Wah setaun te bepisah","Nengke tebedait side sere gagah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116257,"Bau tereng kebon loq Aji","Tereng tebau tepinaq layangan","Ita berdua uwah pada bejanji","Gen ta bedait leq lapangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116258,"Bau timun leq bangket","Ndak lupaq jauq pade","Lamun de' mele selamet","Ndak girang gaweik dose", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116259,"Bau udang gala ngoneq-ngoneq","Laguq mangkin uah engkah","Lamunde uwah jari dengan beleq","Endaq gamaq lueq tingkah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116260,"Bauh mayung lek tengak kebon","Betangkak isik keraro","Timak ndek te besemeton","Ndak lupak timak jak gen lalo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116261,"Baujan di tantang padang","Bataduah di bungka kayu","Manangih batarang pinggang","Bapasan tidak ado urang lalu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116262,"Berhujan di tengah padang","Berteduh di bungkal kayu","Menangis bercekak pinggang","Mau berpesan tiada yang lalu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116263,"Baunya hangit getah diobor","Ganti pelita budak mengaji","Di bawah langit bintang bertabur","Di situ kita meneguh janji.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116264,"Baunya hangit getah diobor","Ganti pelita budak mengaji","Di bawah langit bintang bertabur","Di situ kita meneguh janji", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116265,"Bawa air gelasnya sumbing","Cawan berkarat bawa mudarat","Bagai aur dengan tebing","Hidup sepakat membawa berkat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116266,"Bawa air pakai perian","Air diambil dari perigi.","Maka surat kami kirimkan","Rindu dendam di dalam hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116267,"Bawa aksin terlalu indah","Ombak bersusun berlari-lari","Jika berkata yang tidak berfaedah","Terlebih baik mendiamkan diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116268,"Bawa antan letak di dapur","Letak mari dekatnya sekam","Andai intan berlumur lumpur","Masihkah jauhari kenal manikam?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116269,"Bawa Askin terlalu indah","Ombak bersusun berlari-lari","Jika berkata yang tiada berfaedah","Terlebih baik mendiamkan diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116270,"Bawa baju bawa sepatu","Jalan ramai banyak kendala","Kalau kemiskinan mengetuk pintu","Cinta terbang lewat jendela", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116271,"Bawa bekal ikan sepasu","Puteri rupawan membawa pesan","Bijak akal memantau nafsu","Terbezalah haiwan dengan insan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116272,"Bawa bekal ke medan perang","Nasi kukus sayur kubis","Misai tebal lagaknya garang","Nampak tikus menjerit habis!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116273,"Bawa bekal ketam dan kikir","Tiang rumah kayu meranti","Ada akal tidak berfikir","Mesra ramah kering simpati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116274,"Bawa bekal menebas","Pagi sejuk menjala belanak","Degil nakal diajar sukar","Makan pujuk harimau pun jinak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116275,"Bawa bekal nasi sekepal","Pergi menebang si pokok sena","Cari capal boleh ditampal","Carik hati jiwa merana", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116276,"Bawa bekal pergi permatang","Singgah berhenti memungut salak","Tunggang basikal jatuh terlentang","Kenapa hati geli nak gelak?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116277,"Bawa bekalan ke Padang Lumat","Ikan sepat masak berlada","Melintas jalan mesti berhemat","Bergerak cepat dengan waspada", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116278,"Bawa beluam bersama bokca","Bertandang desa sebelah barat","Bok tilam jadi perca","Kelambu kasa di makan gegat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116279,"Bawa bemo jalan pejambon","Lepas roda dari pegangan","Nasib baik seperti bunglon","Akan berubah dengan lingkungan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116280,"Bawa bersabar jangan termenung","Hati berkocak tidak terduga","Ambillah iktibar mendaki gunung","Tiba ke puncak menurun juga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116281,"Bawa besen pergi telaga","Berisi bilis penuh bercambah","Tiada lesen memandu juga","Nampak polis baru gelabah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116282,"Bawa cebok pergi mandi","Badan pun sedap menjadi segar","Kenapa sibuk menjerat undi","Sehingga ketayap tersangkut di pagar?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116283,"Bawa galah sambil bersembang","Pergi ke kebun mengait betik","Bukan salah bunga tak kembang","Tak ada embun sudi menitik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116284,"Bawa galah tertarik-tarik","Mengait almera di pinggir hutan","Orang salah tentu serik","Kalau penjara mesti dirotan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116285,"Bawa guni ke tempat jemuran","Untuk angkut bijinya lada","Kalau berani langgar aturan","Kenapa takut membayar denda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116286,"Bawa jerami muatan sendat","Singgah di beting tepian laman","Datang kami kunjung beradat","Ingin menyunting bunga di taman", 4, 6);
