INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116288,"Bawa joran ke Alor Malai","Singgah berhenti tepi beranda","Eloknya hantaran dari mempelai","Kami balasi sekadar yang ada", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116289,"Bawa joran ke tepi paya","Di pagi hari pakai ceongsam","Sungguh hairan di hati saya","Senyum diberi balasnya masam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116290,"Bawa joran ke tepi paya","Pakai tudung turun ke sawah","Sungguh hairan di hati saya","Kenapa hidung lubang ke bawah?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116291,"Bawa joran ke tepi paya","Sambil leka melihat burung","Sungguh hairan di hati saya","Kenapa jejaka berambut tempurung?", 4, 6);
