INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116293,"Bawa ke kota ketupat dan kanji","Makan di desa ketika asar","Kata-mengata umpat dan keji","Banyakkan dosa bencana di Mahsyar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116294,"Bawa ke kota ketupat dan kanji","Singgah di desa di lembah baru","Kata-mengata umpat dan keji","Memunggah dosa mencambah seteru", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116295,"Bawa ke kota Raja Haji","Buah berangan bersama lada","Mengukir kata menyusun janji","Semua rancangan akan di ada.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116296,"Bawa ke kota Raja Haji","Buah berangan bersama lada","Mengukir kata menyusun janji","Semua rancangan akan diada", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116297,"Bawa ke pasar buah rambutan","Durian dipajak semasa putik","Kapal besar tiada muatan","Bak buah masak tidak dipetik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116298,"Bawa ke rumah terus digoreng","Dicicah dengan air asam","Buktinya solat selalu ponteng","Jahil tentang kehendak Islam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116299,"Bawa keladi bersama pisang","Daun bayam dikait rapi","Orang berjudi seperti musang","Mengira ayam di dalam mimpi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116300,"Bawa kemari ubi kayu","Hendak menjamu Inche Suri","Inginkan padi yang ada penunggu","Ku patok mata padiku churi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116301,"Bawa kepuk ke tengah sawah","Lesung dan antan diusung sekali","Bujang lapuk berlagak mewah","Sebutir intan tidak terbeli", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (116302,"Bawa kerakap dengan batangnya","Buat bekal pergi ke bendang","Cuba teka apa binatangnya","Berbunyi seperti gong dan gendang", 4, 6);
