INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118015,"Biarlah orang mengaku alim","Alim itu banyak syaratnya","Besarlah malang orang yang zalim","Zalim itu banyak mudaratnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118016,"Biarlah orang mengejar harta","Kita mengejar amal ibadat","Biarlah orang melanggar kata","Kita bersandar pada syariat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118017,"Biarlah orang mengejar pangkat","Kita mengejar kerja yang halal","Besarlah dosa orang yang murtad","Di Padang Mahsyar hidup menyesal", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118018,"Biarlah orang menumbuk bentak","Kita menumbuk padi di peti","Biar orang kemaruk harta","Kita kemaruk budi pekerti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118019,"Biarlah orang merendang angsa","Kita merendam itik segati","Biarlah orang memandang bangsa","Kita memandang nan baik hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118020,"Biarlah orang merendang pedas","Kita merendang berkuah juga","Biarlah orang memandang atas","Kita memandang ke bawah juga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118021,"Biarlah pahat bermata parang","Asalkan badik besi bertuah","Biarlah jahat di mata orang","Asalkan baik di sisi Allah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118022,"Biarlah pahat bermata parang","Asalkan badik besi bertuah","Biarlah jahat dimata orang","Asalkan baik disisi Allah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118023,"Biarlah parang dicanuk lekat","Lekat itu banyak karatnya","Biarlah orang merebut pangkat","Pangkat itu banyak mudaratnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118024,"Biarlah pinang berebut buah","Jika berlaga pecah sendiri","Biarlah orang berebut tuah","Kita menjaga tuah sendiri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118025,"Biarlah pindang berasam cuka","Jika dikais kan manis juga","Biarlah orang bermasam muka","Kita bermanis-manis juga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118026,"Biarlah rajah bercorak batik","Asalkan lorek buatan jati","Biarlah wajah tidak cantik","Asalkan molek timbangan hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118027,"Biarlah retak semua sekali","Asalkan jangan pecahnya lima","Pelbagai rentak irama asli","Dalam ruangan Rampai Irama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118028,"Biarlah sabar dengan perlahan","Itulah tanda tuan beriman","Tidak patah digoncang iblis","Akhirnya tuan menang berjaya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118029,"Biarpoen begitoe ki tjoba tondji","Mentjari hatsil ri pa bojanta","Tjari daja na pila badji","Perbaeki poela pang-mentenganta", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118030,"Biarpun makan belum kenyang","Jangan meminta nasi tetangga","Biarpun badan belum senang","Jangan dicuba mengambil muka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118031,"Biarpun senang di rumah orang","Elok bersusah di rumah sendiri","Biarpun senang karena berhutang","Eloklah bersusah usaha sendiri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118032,"Biasa rejan tanda berbahu","Hendak ingat jangan terkena","Bahasa pantun siapa tak tahu","Banyak manfaat pada yang mengakunya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118033,"Biasa rejan tanda berbau","Hendak ingat sebelum terkena","Bahasa pantun siapa tak tahu","Banyak manfaat yang mengakunya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118034,"Biawak bersisik emas","Bawa penumpang naik haji","Dari Sarawak terus ke Sambas","Mengintai bunga sepuput lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118035,"Bibir melepuh dibawa menyingai","Kera mengail diujung dahan","Liur jatuh menganak sungai","Mata terbendil sebesar lengan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118036,"Bidadari ber-kenan bunga","Petek s'kuntum lindong-an daun","Budi Tuan sahya trima","Jadi batang membilang tahun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118037,"Bidadari dari kayangan","Turun bermain sepak raga","Adinda di taruh limpa jantungan","Tiada ternilai berapa harga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118038,"Bidadari tujuh saudara","Diam di taman Indera Kayangan","Rambang Seri Pangastugara","Kepada kakanda jadi kenangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118039,"Bidadari turun meniaga","Sampai Delhi menjual kain","Mas yang tinggi dapat nya harga","Budi yang baik jangang dipermain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118040,"Bidar hanyut dari Subarang","Langgang jangan ditimba","Sudah badan dibuang orang","Jangan dikenang jangan dicinta", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118041,"Bidara padi dari seberang","Cempedak tangkainya kandis","Mesra di hati juga yang jarang","Jikalau hendak mulutnya manis", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118042,"Bidara padi dari seberang","Cempedak tangkainya kandis","Cinta hati juga yang jarang","Jikalau hendak mulutnya manis.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118043,"Bidara padi dari seberang","Cempedak tangkainya kandis","Cinta hati juga yang kurang","Jikalau hendak mulutnya manis", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118044,"Biduak oleang pangayuah Jambi","Patah dititih kambiang utan","Nantikan Hang badan kami","Mangko diganti jo nan bukan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118045,"Biduk oleng pengayuhnya Jambi","Patah diinjak kambing hutan","Nantikan hilang badan kami","Baru dicari ganti badan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118046,"Biduak sipapan panjang limo","Cari-cari ruang dalam","Tidua bamimpi dalam rimbo","Mananti bulan ka tilam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118047,"Biduk sipapan panjang lima","Cari-cari ruang dalam","Tidur bermimpi dalam rimba","Menanti bulan datang ke tilam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118048,"Biduan menyanyi di dalam istana","Dayang langsing pula menari","Waktu sunyi mencengkam suasana","Ke mana bising melarikan diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118049,"Biduan namo-nyo Ganto Sori","Tinggi-lah atok dari korong","Pantun ka sumbu badan kini","Bak dama dalam telong.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118050,"Biduk beredar perlahan-lahan","Hari redup ke Pulau Langkawi","Qada qadar kepunyaan Tuhan","Tiada makhluk dapat ketahui", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118051,"Biduk buluh bermuat tulang","Anak Siam pulang berbaris","Duduk mengeluh panglima elang","Melihat elang bercengkang keris", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118052,"Biduk buluh bermuat tulang","Anak Siam pulang berbaris","Duduk mengeluh panglima helang","Melihat ayam bercengkang keris", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118053,"Biduk Laksamana menuju barat","Arus menyonsong dari haluan","Pakailah dunia untuk akhirat","Harta yang banyak bakal rebutan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118054,"Biduk laksamana menuju darat","Arus menyongsang dari haluan","Pakailah dunia untuk akhirat","Harta yang banyak bekal rebutan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118055,"Biduk lalu kiambang bertaut","Hanyut perahu ke tepi pantai","Kisah dahulu usah disebur","Sudah tahu hajat tak sampai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118056,"Biduk lalu kiambang bertaut","Nakhoda Kasap duduk bermenung","Gila melatah ikan di laut","Melihat umpan di kaki gunung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118057,"Bijak belayar di angin sakal","Pantas berdayung menongkah arus","Bijak membayar hutang tak kekal","Panas berpayung jalan pun lurus", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118058,"Bijak bersilat bermain pedang","Siap sedia ke medan perang","Azan Maghrib masih di padang","Kelicap pun tahu mencari sarang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118059,"Bijak bestari tiada menyalah","Bila dipegang tidakkan sesat","Syarak berisi Quran dan Sunnah","Dialah tiang dunia akhirat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118060,"Bijak kancil harimau ditipu","Harimau mencakar terlilit akar","Jihad kecil mengawal nafsu","Jihad besar melawan mungkur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118061,"Bijak putera raja di takhta","Cekap menteri rakyat bertuah","Mengaku saudara kerana harta","Tupai mencari dusun berbuah!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118062,"Biji cempedak boleh direbus","Sudah masak sedap rasanya","Hati kemak kasih terputus","Duduk tegak gelap dunianya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118063,"Biji delima dimakan orang","Rasanya kelat bercampur masam","Hati merana bukan kepalang","Dinda ku ingat siang dan malam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118064,"Biji durian jangan dibuang","Supaya dapat ditanam lagi","Hati rawan bukan kepalang","Dinda ku lihat didalam mimpi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118065,"Biji embacang ada bersabut","Sabutnya jangan pula ditelan","Menanti abang nyawa tercabut","Hanyutlah badan dalam kesepian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118066,"Biji lada ditabur-tabur","Hari ketiga tumbuh merecup","Benih kasih sudah berkubur","Bunga cinta layu kuncup", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118067,"Biji lada mari di asah","Hendak mengubat ketagih ganja","Untung ada ditangkap basah","Nikah dapat ta'kena belanja.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118068,"Biji lada mari diasah","Hendak mengubat ketagih ganja","Untung ada ditangkap basah","Nikah dapat tak kena belanja", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118069,"Biji macang sebiji bulat","Dikupas si jari manis","Ada abang berkirim surat","Belum dibaca sudah menangis.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118070,"Biji mentimun dibuat bibit","Sampai musimnya ditanam lagi","Menanti bertahun membuat sakit","Badan merana dendam berahi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118071,"Biji nangka jangan dibuang","Bila dibuang akan merugi","Hati cinta jangan dihalang","Bila dihalang membawa mati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118072,"Biji nangka jangan ditelan","Bila ditelan tentu tercekik","Hatiku duka putus harapan","Kerana lama rindukan adik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118073,"Biji pauh ditanam orang","Sudah besar berbuah pula","Hati rusuh bukan kepalang","Habislah sabar menanti dinda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118074,"Biji saga warnanya merah","Lalu disimpan didalam peti","Budi disemai bakti dicurah","Kemana pergi akan dihormati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118075,"Biji selaseh jadi jualan","Habis di-jual bersama ragi","Sebab kaseh putus di-jalan","Kerana si-dia ta' sudi lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118076,"Biji selasih bersama pegaga","Dalam baldi di atas guni","Terima kasih tidak terhingga","Baik budi kerabat di sini", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118077,"Biji selasih bersama pegaga","Di dalam baldi bersama kuini","Terima kasih tidak terhingga","Baik budi kerabat di sini", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118078,"Biji selasih diatas dulang","Sudah diayak lalu direndam","Kunanti kekasih tiada datang","Dimana tegak tak mau diam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118079,"Biji selasih didalam peti","Sampai masanya dijerang juga","Sekali kasih didalam hati","Sampailah tua ku kenang juga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118080,"Biji selasih direndam air","Bila dihirup rasanya sedap","Hatiku pedih sayang berakhir","Selama hidup putusla harap", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118081,"Biji selasih lagikan putih","Serangkai nona mari dijinjing","Mengapa masih berpatik-patik","Sedangkan ma'ana si anak anjing.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118082,"Biji selasih lagikan putik","Serangkai nona mari dijinjing","Mengapa masih berpatik-patik","Sedangkan makna si anak anjing", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118083,"Bijian lada di atas para","Hitam warna sudah bersukat","Ayah bonda pengantin dara","Sudi kiranya merenjis berkat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118084,"Bikin pengat buah kladi","Masak santan buah payapa","Sunggu pun banyak kita berdudi","Mana-kan sama orang yang kaya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118085,"Bikin pengat daum kladi","Masak santan dengan pepaya","Sunggu banyak kita ber-budi","Mana sama orang kaya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118086,"Bikin rumah di Praman","Tiang nya gahru chendana jati","Sunggoh banyak kuntum di taman","Chempaka biru pagar beroti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118087,"Biku biku di sapu tangan","Di pakay kan anak raja raja","Barang yang tentu berpanjangan","Takkan bolay di buat sengaja", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118088,"Bila ada jarum yang patah","Jangan dipatah jarum yang satu","Bila ada pantun yang salah","Jangan hamba diberi malu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118089,"Bila ada makanan di lemari","Sajikan dan makan bersama","Bila terasa susah di hati","Baca Quran tenang semula", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118090,"Bila badik berkering basah","Tentulah mudah dimakan karat","Bila adik berpaling tadah","Tentulah hamba takkan mendekat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118091,"Bila badik dibasah orang","Tentu tanda melawan musuh","Bila adik berpatah arang","Tentu hamba akan menjauh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118092,"Bila badik jadi senjata","Siang malam hati berani","Bila adik berjanji setia","Abang pendam dalam hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118093,"Bila badik sudah basah","Tentu senang membela diri","Bila adik sudah bersumpah","Tentulah abang setia menanti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118094,"Bila bakar padi di huma","Bawalah lada ke Gunung Daik","Bila mendengar lagu lama","Ke zaman muda rasa nak balik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118095,"Bila batang tidak berakar","Batang lapuk dahan meranggas","Bila hutang tidak terbayar","Sebarang duduk merasa cemas", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118096,"Bila batang tidak berakar","Buah gugur kuncup pun layu","Bila hutang tidak terbayar","Marwah hancur hidup pun malu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118097,"Bila batang tidak berakar","Masih kuncup sudah rebah","Bila hutang tidak terbayar","Selama hidup ditimpa musibah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118098,"Bila batang tidak berakar","Tentulah tumbang jatuh ke tanah","Bila hutang tidak terbayar","Seteru datang musuh bertambah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118099,"Bila beramal tidak berilmu","Bagaikan berlayar tak ada pedoman","Bila akal tidak dipandu","Kerja yang benar akan menyeman", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118100,"Bila berbelah menjadi kayu","Bila terkerat mohon elokkan","Bila tersalah berilah tahu","Bila tersesat mohon luruskan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118101,"Bila berburu mengejar rusa","Pakai senjata ataupun jerat","Bila berguru besar kepala","Sampai tua takkan mendapat", 4, 6);
