INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118471,"Bli gula berkati kati","Handak bikin siray kaya","Pokok pangkal deri hati","Tidak pilay miskin kaya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118472,"Bli gula satu tempayan","Gula berchampor ayer madu","Kalu ada blas kesian","Disitu lah tempat saya mengadu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118473,"Blon mera tengok di katja","Katja lipat di dalam petie","Kendatie djaoe la deri mata","Asal djangan djaoe di atie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118474,"Bocor bumbung patah gelegar","Hilang tempat hendak menyorok","Jahil sombong susah diajar","Bagai menampal perahu buruk", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118475,"Bocor perahu gala dipesan","Perahu ditunda ke pangkalan","Bersuluh ilmu pelita insan","Terang hati bertambah amalan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118476,"Bodo gabah leq Jerowaru","Pinaq api leman lilin","Lamunda mauq batur baru","Ndaq lupaq bareng siq lain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118477,"Bodoh alah lagak Pak Pandir","Kering perigi kain dicuci","Menyirap darah bila disindir","Belum lagi tuan dicaci", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118478,"Boea paa die tinggie tarie","Koera-koera die dalam padie","Tjieoem sie Nonna tandaja kasie","Kiera berkiera die dalam hatie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118479,"Boea pepare' bertalie talie","Soeda toea tjampoe soelasie","Bea djoega djadiken sekalie","Soeda lama badan di kasie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118480,"Boea semangka bidjienja itam","Kieriman lagie toelan di Bantan","Kiat nona tidoerkan baoen","Sebagei maas di taboer intan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118481,"Boeah ara","batang dinantoen","Mari dibantoen dengan parang","Hai saudara dengarlah pantoen", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118482,"Boeai-boeai dalam boeiaian","Boeaian dari rotan saga","Pandjang benar djanggut toean","Mari diboeat tali rimba", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118483,"Boeat apa katja di pientoe","Saya mienta katja di bale","Boeat apa kata begitoe","Saya mienta kabar jang bae", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118484,"Boeat apa selasie daon","Saija minta selasie roempoet","Maen moeda djangan setaon","Saija mienta saoemoer idoep", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118485,"Boeat apa selasie daon","Saija minta selasie roempoet","Maen moeda djangan setaon","Saija mienta saoemoer idoep", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118486,"Boeaya tiemboel koee sangka matie","Ole koe sangkan la batang piesang","Baek koe boedjang la sampe matie","Matie koe tienggal die tangan toean", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118487,"Boeelan tiga matahari doea","Poetrie tjiena menjepoer kaen","Nona moeda sayapon moeda","Sama sama soeka bermain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118488,"Boekan katjang sembarang katjan","Katjang melieliet die dadang poela","Maskie nona die moeloet matjam","Saija tjarie sabole bole", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118489,"Boekan katjang sembarang katjang","Katjang melilied di batang padie","Boekan datang sembarang datang","Datang menengoek si djantoeng atie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118490,"Boekan keyong sebarang keyong","Katjang melilied di badang padie","Boekan tjioem sembarang tjieom","Tjioem di pipie teroes di atie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118491,"Boekan keyong sembarang keyong","Keyong melilied di batang padie","Boekan tjioem sembarang tjioem","Tjioem di piepie teroes di atie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118492,"Boelan boendar ranke galoean","Koembang mentjarie di daon djatie","Itoe elo garangan toean","Ibarat piso ieries di atie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118493,"Boelan menoeroen te bintang sieang","Kasie toean djangan kepalang","Ramboet sie Nonna die tindies toean","Soeda la makan mahkota sie Nonna poelang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118494,"Boeloenja jato kapatanie","Doea poeloe anak merpatie","Tieda sama moeda ko hinnie","Soengoe pande manboejoeng atie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118495,"Boenga kalemkak goegoer sariernya","Boenga papaya derier tjekienie","Sepertie ombak banieng dirinja","Mengapa saya giela begienie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118496,"Boenga kenanga die atas gere","Terang boelan die tanga oetan","Bagie la mana bole batjare","Inie la ada setangke kembang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118497,"Boenga melatie die dalam taman","Boenga kalamkak goegoer sarienja","Atie die dalam tiedakan senang","Sepertie ombak banting dierinja", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118498,"Boenga pala la darie Jambie","Palla datang la darie Girie","Boekan sala la darie kamie","Sala djoega derie sendierie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118499,"Boenga pandan anjoet ka Djawa","Batang boenjoek alasnja padie","Selagie badan tiada ketjewa","Soeka katemoe panghiboer atie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118500,"Boenga pandan dalam soeratie","Makanan boerong radja soerana","Kapandang moeda minken biraie","Sebagie orang la kena goena", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118501,"Boenga soelasie di atas gere'","Daon pandan lagie terboeka","Sampe ka matie baroe betjere'","Kita doea maoe bersoempa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118502,"Boeroeng bangau terbang ke Barat","Hinggap di ranting pohon mengkoedoe","Terimalah salam dagang jang larat","Siang dan malam menangoeng rindoe", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118503,"Boeroeng gagak makannja padie","Padie raroean die tana itoe","Bela dada meliat atie","Atie doea menjadie satoe", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118504,"Boeroeng garoeda terbang die batoe","Pake poepoer langar die pipie","Sebab ingat sie nonna itoe","Dalam tiedoer dapat mengimempie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118505,"Boeroeng garoeda terbang ka badan","Boeloenja lagie jato kapatanie","Banjag moeda soeda kopandang","Tieda sama moeda ko inenie", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118506,"Boeroeng mera di atas roema","Marie koe soempit kena dadanja","Djieka sie Nonna mendjadie boenga","Marie koe soenping la slamanja", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118507,"Boeroeng serindit terbang melajang","Mari hinggap ranting mati","Boekan ringgit pandangan orang","Pandangan di atas bahasa boedi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118508,"Boeroeng serindit terbang melajang","Singgah hinggap ranting mati","Doeit ringgit dipandang orang","Djarang dipandang bahasa boedi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118509,"Bogor Lateh simpangnya empat","Atas permatang budak berlari","Kalau boleh biarlah dapat","Kalau kecundang dicuba lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118510,"Bohor Ibus singgah di kedai","Tumbuh lalang akar melata","Hendak ditebus bukan tergadai","Inikan hilang apa nak kata", 4, 6);
