INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118515,"Bola ada boleh disepak","Di lapangan orang bermain","Di akhir jalan setapak","Jalan besar baru mulai", 4, 6);
