INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118518,"Bola jaring bola lesut","Masuk padang sarungkan kasut","Muka masam rambut kusut","Bawa hajat besar maksud!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118519,"Bola kaki ditendang orang","Tempat mainnya hendaklah luas","Bila ku mati mengenang abang","Ingatlah cinta yang tidak lepas", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118520,"Bola perada di dalam perahu","Jatuh melantun ke dalam air","Kenapa ada masih tak tahu","Beza pantun dengan syair?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118521,"Bolai dibao cangkiang runtuah","Urek manjulai ka subarang","Lah buliah cindai panjang tujuah","Tabuang kain kasa jarang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118522,"Bolang panic siq wah tepong","Mula ta kadu leq selametan","Jari dengan dendeq ta sombong","Siq bedue haq ino hanya Tuhan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118523,"Bole tetendang jeri mental","Keras laloq tetendang jari pesok","Dendeq pede girang besual","Si kalah bocor", 4, 6);
