INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118527,"Boleh dipetik buah mengkudu","Asal jangan buah pepaya","Biar lupa kain dan baju","Asal jangan kepada saya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118528,"Boleh ditajak jangan ditebang","Rumput semalu di celah lalang","Hendak diajak tidak bertambung","Sabar menunggu abangmu pulang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118529,"Boleh ditebang tebu muda","Tebu tua dahulu tebang","Boleh saja beristri dua","Dalam islan tidak dilarang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118530,"Boleh ditonton 24 jam","Akademi Fantasi jadi tarikan","Menyesal bila mata pejam","Baru akui kebenaran Al-Quran", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118531,"Boleh juga belayar kapal","Patah galah apa galahnya?","Boleh juga bertukar pakai","Kalau bujang apa salahnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118532,"Boleh maju hal-hal dunia","Isu Fardhu Ain selesai dulu","Majoriti dari bangsa kita","Disebabkan hilang sifat malu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118533,"Boleh membuka rahsiaku","Duka cita dalam dadaku","Dunia ini saja ku pinjam","Dag en nacht saya menimbang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118534,"Boleh mengeratkan tali persaudaraan","Digalakkan Al-Quran dan Hadis","Nafsu amarah menguasai perasan","Hidup cara Hedonis para gadis", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118535,"Boleh menyatukan masyarakat majemuk","Dengan dasar adil dan saksama","Bagi yang solat dengan khusuk","Berjihad menegakkan ajaran agama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118536,"Boleh menyeberang ke Kota Baharu","Naik kapal sebentar sahaja","Pura-pura mendatangkan keliru","Susah majunya segala kerja", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118537,"Boleh saja makan sedikit","Walaupun sedikit membawa kenyang","Kasih hamba bukan penyakit","Walaupun sakit kerana abang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118538,"Bolehkah dagang memberi cerana","Lengkap dengan sirih pinangnya","Bolehkah dagang datang bertanya","Adakah tempat di kebun juwita", 4, 6);
