INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118544,"Bolehkah ke sana kalau berkenaan","Kalau tidak pergilah ke taman","Tanah Melayu mencapai kemerdekaan","Rakyat hidup dalam aman", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118545,"Bom meledak di Sari Bali","Banyak korban orang yang mati","Alasan bisa dicari-cari","Kita menampar muka sendiri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118546,"Bongkar muat","Sampan laju berlayar","Janji dibuat","Utang belum terbayar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118547,"Bongkar sauh ke Tanjung Pinang","Orang Sarawak berjual kain","Tempat jatuh lagi dikenang","Kononlah pulak tempat bermain", 4, 6);
