INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118549,"Bongkar sauh kembangkan layar","Orang berjudi atas lamat","Hutang emas boleh dibayar","Hutang budi hati terikat.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (118550,"Bongkar sauh kembangkan layar","Orang berjudi di daun sakat","Hutang emas boleh dibayar","Hutang budi hati terikat", 4, 6);
