INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120274,"Bulan Ramadhan kuat beribadat","Suka hadir di majlis tazkirah","Dalam syurga penuh nikmat","Balasan yang betul aqidah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120275,"Bulan Ramadhan sudah dikira","Bulan mulia bulan puasa","Berbaktilah kita kepada Negara","Jadikan amalan setiap masa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120276,"Bulan sabit di langit tinggi","Sayup-sayup mata memandang","Sudahlah nasib celaka diri","Adik ku cinta dipinang orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120277,"Bulan sabit diambang petang","Makin dipandang makin indah","Sudah nasib abang yang malang","Hendak meminang adik lah nikah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120278,"Bulan sabit sebelah hulu","Cahaya memudar kena matahari","Badan ku sakit menahan rindu","Adinda ingkar kepada janji", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120279,"Bulan sine musim panas","Bulan jemak muslim nyet","Ndak ngingon acong ganas","Lebih bagus te ngingon monyet", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120280,"Bulan Syafar bulan kedua","Sesudah itu Rabi'ul awal","Menahan lapar tidak mengapa","Patah hati sampailah ajal", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120281,"Bulan terang angin pun sedap","Bunga keduduk di dalam bendang","Tuan sudah terkena injap","Bubu duduk jiwa 'kan hilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120282,"Bulan terang belayar kolek","Anak lipas lipas di hujung galah","Dunia sekarang sudah terbalik","Pencuri terlepas", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120283,"Bulan terang bintang berantai","Anak Keling bermain api","Kalau sekiranya tuan rasai","Gunting bermain dalam hati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120284,"Bulan terang chaya berkilatan","Chaya meliputi dalam paya","Hendak pulang semangat di simpan","Angin tiop mata nak laya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120285,"Bulan terang chuacha di pulau","Raja Siam turn berakit","Rindu dendam bawak bergurau","Jangan sampai jadi penyakit", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120286,"Bulan terang di Pulau Upih","Tempat orang menjemur pinggan","Saya umpama kain putih","Apa suka tuan warnakan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120287,"Bulan terang di-Pulau Upeh","Tempat raja menjemor pinggan","Saya sa-umpama kain puteh","Apa suka tuan warnakan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120288,"Bulan terang ditengah laut","Boleh saya pancing gelama","Kalau datang ajal dan maut","Mahukah tuan mati bersama?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120289,"Bulan terang hujan pun lebat","Air mengalir sampai ke laut","Luka di tangan boleh diubat","Luka di hati membawa maut", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120290,"Bulan terang hujan pun lebat","Aying mengalir sampai ke laut","Luka di tangan boleh diubat","Luka di hati membawa maut", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120291,"Bulan terang labi bermain","Anak muda jalan bersuluh","Kita laksana baju dan kain","Takkan renggang dari tubuh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120292,"Bulan terang lagi bersuluh","Berlatih kompang riuhnya kampung","Dulu seorang merintih mengeluh","Kini riang kasih bersambung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120293,"Bulan terang menikam babi","Tertikam ke anak harimau","Orang seberang turun mandi","Pinjam tangan meramas limau.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120294,"Bulan terang tak terang sangat","Cahaya sampai kat muka pintu","Nak kata sayang tak sayang sangat","Adat berkasih memang begitu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120295,"Bulan terang taram-temaram","Hantu berjalan laki bini","Jangan lah saya di aram-aram","Saya dagang tercampak di sini", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120296,"Bulan terang bintang berantai","Budak Kelantan pergi mengaji","Andai tuan dapat rasai","Gunting bermain dalam hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120297,"Bulan terbit penuh purnama","Mengail keli di Gelang Patah","Kelapa sawit tanaman utama","Selain daripada hasilan getah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120298,"Bulan terbit senja di ambang","Cuaca mendung pasangkan lilin","Telinga rabit dipakaikan subang","Si kudung dibelikan cincin?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120299,"Bulan terbit terang sebelah","Awan mengasak hujan bertabur","Hilang sabit patah galah","Bunga cinta menunggu gugur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120300,"Bulan tiga matahari dua","Puteri cina menyempur kain","Norma muda saya pun muda","Sama-sama suka bermain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120301,"Bulan timbul di atas gunung","Tenggelam di bawah lautan","Rasa senang mulai bertumbuh","Kasih sayang bisa berkembang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120302,"Bulan trang angin pun sedap","Bunga k'dudok di-dalam bendang","Tu'an sudah terkena ininjap","Bubu dodok jiwa-kau hilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120303,"Bulan trang di dalam paya","Nampak deri tanjong Sebrang","Tidak orang sperti saya","Menabor budi chuma terbuang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120304,"Bulan trang di dalam paya","Bunga nanas di dalam kubun","Tidak orang sarupa saya","Siang bupanas malam burumbun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120305,"Bulan trang di Pulau Batam","Anak Malayu pasang jaring","Baik-baik tuan menyelam","Kapala jangan terkena beting", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120306,"Bulan trang di puloh upay","Tempat raja menjemor pinggan","Saya sempama kain yang putay","Apa suka tuan orna kan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120307,"Bulan trang di tenga laut","Sukat garam tiga gantang","Kalu tidak arah di laut","Ka-dua bulan ka-tiga datang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120308,"Bulan trang di tenga laut","Sukat garam tiga gantang","Kalu tidak arah di laut","Ka-dua bulan ka-tiga datang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120309,"Bulan trang di tenga paya","Kayet kayet didalam dulang","Tidak orang s'bagi sahya","Dudok ber-pikay karang s'orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120310,"Bulan trang di tenga paya","Tali ramay di dalam dulang","Tidak orang sebagi saya","Dudok ramay karam sorang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120311,"Bulan trang di tengah malam","Anak prawan jalan berlengang","Chinta kan tuan pilu di dalam","Salayra badan merasa rengang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120312,"Bulan trang di tengah paya","Tali ramay di dalam dulang","Tidak orang sperti saya","Dudok ramay karam sa-orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120313,"Bulan trang di tepi paya","Bunga nanas di tenga kebun","Tidak orang s'bagi sahya","Siang ber-panas malam ber-umbun.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120314,"Bulan trang di tepi paya","Goreng kachang jangan angus","Tidak orang sperti saya","Sebagi panchang di gonchang arus", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120315,"Bulan trang trang termaram","Hantu berjalan laki bini","Jangan tu'an di-aram-aram","Sahya tidak lama di-sini.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120316,"Bulang makalangan penyu metaluhan","Katak dongkang becing-becing","Buin pidan nira kema","Tampahanga cicing bengil", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120317,"Bulang trang deri sebrang","Chaya sampay ka-bawa tana","Alang sakit berdendam sorang","Sbagi bisol mengangkot nana", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120318,"Bulat leher jahitan kemas","Siku keluang sulam kerawang","Tuahmu lahir di dulang emas","Banyak peluang banyak wang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120319,"Bulatan Ilmu penuh kereta","Berdedai-dedai menuju pulang","Manusia hidup bercita-cita","Kecewa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120320,"Bulek kato dipangkanyo","Pacah kato diujuangnyo","Sulik pahamnyo kato-kato","Adapun maratabatnyo tigo parkaro", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120321,"Bulek ruponyo daun nipah","Buleknyo nyato bapasagi","Diliek lipek tak barubah","Lah dibukak tambuak tiok ragi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120322,"Bulek sagiliang picak satapiak","Lah bulek aia di pambuluah","Bulek kato di mupakaik","Di sinan putusan dipacik taguah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120323,"Buli gula satu tumpayan","Gula burchampor ayer madu","Jikalau ada burlas kasihan","Disitu tumpat kita mungadu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120324,"Buli-buli di atas pintu","Mualim menyurat di mihrab","Elok duduk di dekat guru","Supaya terang jalan ke akherat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120325,"Buli-buli di ate pintu","Malin manyurek di mahirab","Elok duduk di dekat guru","Supaya terang jalan ke akherat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120326,"Buli-buli di ate pintu","Malin manyurek di mahirab","Elok duduok di dokek guru","Supayo torang jalan ka akherat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120327,"Buloh betong tumbang lama","Tak dak siapa tetak lagi","Tumboh tembun bujang lama","Tak dak sapa mintak lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120328,"Buloh butong di kurat tiga","Sayang sekurat di makan api","Untong suda purminta kita","Apa mau di susal lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120329,"Buloh di raot buang sembilu","Banyak alus serta umpuloh nya","Puloh di laut beribu ribu","Dimana tuju ada alor nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120330,"Buloh di raot tengah padang","Tersangkot di pokok kayu bidara","Puloh yang anyot ter-adang adang","Andak mengikot tak tentu ara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120331,"Buloh di tetak membuat gala","Mari di buboh dalam prau","Terpasang plita sinja kala","Satu maklop haram tak tau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120332,"Buloh di-raot buang semilu","Semilu di-raot dengan empeloh-nya","Puloh di-laot beribu ribu","Dimana tuju ada perdoman-nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120333,"Buloh di-raot tenggah padang","Sudah di-raot lalu di-belah","Puloh hanyot teradang adang","Handak di-ikot serba salah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120334,"Buloh mengkasar bawak ka-China","Daon-nya habis di-makan api","Puloh yang besar tentu dudok-nya","Puloh yang kerchik hanyut ka-tepi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120335,"Bulu betung ditebang rata","Banyak tercampak dalam air","Sungguh beruntung orang buta","Tidak nampak orang mencibir", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120336,"Bulu diambil dibuat kipas","Kipas terletak bersama dokoh","Belum dicium dendam tak lepas","Di dalam cinta hendak berjodoh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120337,"Bulu godang di dolok amang","Na magulang tu alaman","Patibu ma magodang amang","Anso kehe hita tu tulangman", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120338,"Bulu gunjo ambiak ka saga","Kalam patah dawat taserak","Jokok basuo sumbang jo jangga","Barilah maaf banyak-banyak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120339,"Bulu kuduk bila merinding","Tandanya dekat syaitan menggoda","Istri bukanlah segumpal daging","Dapat di kerat semena-mena", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120340,"Bulu landak digunting Patani","Jatuh ke kaki lukalah tangan","Hati hendak mata berahi","Tidaklah lupa di angan-angan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120341,"Bulu lorek si ayan kampung","Duduk mengerang dalam kekabu","Sulit menarik rambut di tepung","Sulit lagi memikat hatimu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120342,"Bulu putih si ayam denak","Bulu balik si ayam jantan","Air jernih ikan pun jinak","Perangai baik jadi ikutan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120343,"Buluh apus hanyut terbiar","Hingga ke teluk tepian titi","Usah dipupus hidupan liar","Hendaknya makhluk kita simpati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120344,"Buluh aur jangan dibelah","Titi dibuat ikatkan akar;","Teguh setia tidak berubah","Janji ikrar tidak bertukar.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120345,"Buluh bernama buluh perindu","Tumbuh dibukit pinggir jalan","Tahukah anda seorang ratu","Yang di takluk Nabi Sulaiman", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120346,"Buluh besar dibuat galah","Diberi berkaki penyangga tangga","Sungguh besar nikmat Allah","Diberinya rezeki tiada berhingga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120347,"Buluh betuh batangnya teduh","Jika berduri ditetakkan orang","Sungguh beruntung orang bodoh","Kemana pergi digelakkan orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120348,"Buluh betung badan beruas","Batang beratur duri berduri","Sungguh beruntung orang pemalas","Senang tidur meninggi hari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120349,"Buluh betung batang berduri","Di buat lantai gelegar pengapit","Ibarat umpama burung kedidi","Di mana pantai tercunggit-cunggit.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120350,"Buluh betung batang berduri","Dibuat lantai gelegar pengapit","Ibarat seperti burung kedidi","Di mana pantai tercunggit-cunggit", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120351,"Buluh betung batangnya teduh","Ada berduri ditebang orang","Sungguh beruntung orang bodoh","Kemana pergi digelakkan orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120352,"Buluh betung berbusu-busu","Buluh kasap di tepi rawa","Sungguh beruntung orang bisu","Disuruh bercakap awak tertawa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120353,"Buluh betung bercabang rebung","Batangnya tidak berbelah lagi","Sungguh beruntung orang bodoh","Orang berunding awak tertidur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120354,"Buluh betung berdekak-dekak","Ayam berbunyi di tepi dapur","Sungguh beruntung orang pekak","Meriam berbunyi awak tertidur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120355,"Buluh betung berdengung-dengung","Ditebang terguling serak berserak","Sungguh beruntung orang nan bingung","Orang berunding awak terberak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120356,"Buluh betung di tepi pondok","Terbakar pohon tidak kan hangus","Sungguh beruntung berbini gemuk","Lapar setahun tidakkan kurus", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120357,"Buluh betung dibuat pagar","Batangnya tinggi tumbuh pun cepat","Orang sombong cakapnya besar","Kemana pegi orang mengumpat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120358,"Buluh betung ditebang jatuh","Tumbang ke bumi tak ada tegak","Sungguh beruntung orang lumpuh","Orang berlari awak merangkak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120359,"Buluh betung ditebang rata","Banyak tercampak di tepi tubir","Sungguh beruntung orang buta","Tidak nampak orang mencibir", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120360,"Buluh betung ditebang rubuh","Tumbang ke bumi patah berderak","Sungguh beruntung orang lumpuh","Ditantang berlari awak merangkak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120361,"Buluh betung rebungnya banyak","Bila digulai terasa perisa","Sungguh beruntung orang yang bijak","Percaya diri teguh pendiriannya", 4, 6);
