INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120990,"Bungkuih banamo saputangan","Babuhua ujuang ka ampeknyo","Buruang banamo ganggo aman","Musim pabilo ka dapeknyo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120991,"Bungkus bernama sapu tangan","Berbuhur ujung keempatnya","Burung bernama gangga aman","Musim pabila akan dapatnya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120992,"Bungkus bawang di kain basah","Pergi ke pasar membeli kipas","Mau dibuang terlalu susah","Cinta berahi belum terlepas.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120993,"Bungkus nagke kadu karung bedah","Seduh kupi kadu aiq beneng","Lamunte girang gaweq ibadah","Leq akhirat gente seneng", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120994,"Bungkus sirih di daun waru","Pinang dan kapur ada tempat","Jalanan bukan hanya satu","Jalan yang lain bukan sesat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120995,"Bungo campago kambang biru","Tumbuah di Pulau Karek Rotan","Bukan murah manahan rindu","Bak aia ditampuah sampan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120996,"Bunga cempaka kembang biru","Tumbuh di Pulau Karek Rotan","Bukan mudah menahan rindu","Bagai air ditempuh sampan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120997,"Bungo campago kok nyo kambang","Kambang nan tidak kucuik lai","Burung dewata kok nya tabang","Tabang nan tidak suruik lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120998,"Bunga cempaka kalau kembang","Kembang tidak kuncup lagi","Burung dewata kalau terbang","Terbang nan tidak surut lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (120999,"Bungo campago satu alai","Tumbuah di kubua Tuan Aji","Aia mato salamo carai","Elok ka sumua bakeh mandi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121000,"Bunga cempaka satu helai","Tumbuh di kubur Tuan Haji","Air mata selama cerai","Elok untuk sumur tempat mandi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121001,"Bungo culan tumbuahnyo tinggi","Sariklah puti mamatiaknyo","Parampuan mudah dicari","Nan babudi jarang basuo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121002,"Bungo inai bungo nangko","Pacah pinggan ateh pamatang","Mangko di karang surek iko","Rindu dandam bukan kapalang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121003,"Bungo malua bungo malati","Katigo bungo pandak kaki","Kok tak dapek nan kandak ati","Usah banamo laki-laki", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121004,"Bunga melur bunga melati","Ketiga bunga pandak kaki","Jika tak dapat kehendak hati","Usah bernama laki-laki", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121005,"Bungo Sitatang di halaman","Mari rang paga jo lulito","Anak rang Padang Pariaman","Itu nan tidua lalok siko", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121006,"Bungong rom bungong rihan","Bunga peukan nyang mangat bee","Tameukawen nyang sipadan","Tameurakan nyang tob meunalee", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121007,"Buno linta di atas pasir","Di makan gagak bawak berlari","Barang di mata jangan di taksir","Bukan harga bertawar bli", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121008,"Bunoh jani dipeda ati","Lempedu manah ati muting geraji.","Enti ati manah sanuju enggau mimpi","Baka ke diator kedeka ati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121009,"Buntal pulut namanya padi","Padi berasal Bukit Siguntang","Begitu di mulut begitu di hati","Kasih kekal adinda seorang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121010,"Bunting galoh sembilan bulan","Mengidam makan sayor kladi","Suda jaoh bertamba rawan","Kasay tidak di uba lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121011,"Bunyi apa tengah malam","Kuat-nya bedok deri hulu","S'blom Allah tempah-kan Adam","Tersurat sudah terlebeh dulu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121012,"Bunyi apa tengah malam","Kuat-nya bedok deri hulu","S'blom Allah tempah-kan Adam","Tersurat sudah terlebeh dulu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121013,"Bunyi apa tengah malam","Kuat-nya bedok dari ulu","Ser-belom Allah tempah kan Adam","Ter-surat sudah terlebeh dulu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121014,"Bunyi bahana paluan gendang","Gaung bergema enak didengari","Suami laksana pohonan rendang","Penaung utama anak isteri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121015,"Bunyi bedok deri bandar","Di pangil serta orang ka sana","Selagi hidop kita bersandar","Sampay masa pulang di tana", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121016,"Bunyi beduk waktu zuhur","Memanggil umat berhimpun","Orang berbudi luhur","Menyentuh hati siapa siapa pun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121017,"Bunyi berbisik angin semilir","Sejuk menyusur bagai berkepak","Hati bersih bak air mengalir","Hati kotor bak air di lopak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121018,"Bunyi berdebab sebrang pekan","Pipit tuli kena di jerat","Bua maboh jangan di makan","Pokok berduri jangan di panjat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121019,"Bunyi berderap suara di-padang","Serta pedang senapang peluru","Harap jugak bunga kembang","Kembang tak lama berbalek layu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121020,"Bunyi cacing bunyi cengkerik","Cengkerik berlagu menyambut senja","Sekali jatuh janganlah serik","Bangun lagi mulakan kerja", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121021,"Bunyi cicak di tengah malam","Datang mengkarung tumpang berbaring","Air beriak tanda tak dalam","Tong kosong bunyinya nyaring", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121022,"Bunyi gong yam-tuan besar","Di sambot locheng yam-tuan Muda","Lautan kring gunong berkisar","Chaching berkubang sambar gorda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121023,"Bunyi guntur disangka hujan","Air tempayan ditumpahkan","Pikir dulu sebelum bilang","Salah kata bisa berhantam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121024,"Bunyi guruh angin menderu","Petir kilat diwaktu petang","Kalau jauh ia diburu","Setelah dapat lalu ditendang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121025,"Bunyi guruh di Tanjung Tuan","Bunyi melantas ke Selayang","Jika terjamah benda bertuan","Alamat nyawa akan melayang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121026,"Bunyi guruh hujan diramal","Air di tempayan dibuang jangan","Menambah ilmu lalu beramal","Buat baik tinggalkan larangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121027,"Bunyi parau katak di paya","Riuh meriah di malam lebaran","Cantik di luar nampak bercahaya","Hiasan rumah bacalah al-Qur'an", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121028,"Bunyi pungguk dayu mendayu","Hinggap diranting lata melata","Lapuklah sebelah lengan baju","Kerana menghapus air mata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121029,"Bunyi ribot semalam tadi","Anyot kapal deri kuala","Tidor mengigoh semalam tadi","Bantal terchampak deri kapala", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121030,"Bunyi trompet raja dinobat","Tuan puteri dalam istana","Sakit di kulit disapu ubat","Sakit hati jiwa merana", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121031,"Bunyinya gamat gendang dan tabuh","Tenang damai di malam sunyi","Bangun lewat terlepas Subuh","Salahkan jam tidak berbunyi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121032,"Bupa-bupa salasih dani","Kasih-kasih pakukan jamban","Cuba-cuba kasihkan kami","Kalau banyak ambilkan zaman", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121033,"Bupuchok rosak kan dahan","Bubunga puchok nya mati","Sabab prot seksa kan badan","Sayang kan jiwa takot mati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121034,"Burak keinderaan pada junjungan","Sangat mulia tiada terperi","Selama tiada berpandangan","Tiadaku lupa barang sehari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121035,"Burger mendengarlah itu kata","Lalu bercucurlah air mata","Aduh kesianlah kapten kami","Biar ku mati seramai-ramai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121036,"Burger sampai dengan selamat","Pada memandang sahadat tanah","Serta anak dan saudaranya","Serta semua keluarganya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121037,"Burger turun injak di pantai","Pada ketikalah sudah sampai","Ketika itulah hujan datang","Burger berlindunglah di jambatan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121038,"Burong ampat suara nya anam","Dia terbang tinggal kan sarang","Sakit di obat mati di tanam","Itu la budi di kenang orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121039,"Burong belibeh terbang belibeh","Singgah mandi di Aloq Pulai","Tengok boleh nak ambik tak boleh","Seperti kilat di ujan renyai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121040,"Burong belibeh terbang belibeh","Singgah mandi di Aloq Pulsi","Tengok boleh nak ambik tak boleh","Seperti bunga di ujung julai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121041,"Burong beriat burong di hutan","Bediri di ataih rantin mati","Tengok oghang sambey makan","Lepaih makan terbang pi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121042,"Burong botak di-pokok ara","Melihat kuda berlari-larian","Kalau ta' mahu katakan segera","Jangan kanda nanti-natikan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121043,"Burong dewata tinggi mengawan","Sunggoh garang-nya di-pandang mata","Jawab-nya mata pemberian tuhan","Ta' ada-nya kelam-nya dunia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121044,"Burong di-pohon berembang","Bunyi-nya garang menakutkan hati","Kalau bagitu katamu abang","Baru-lah senang rasa di-hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121045,"Burong gagak baru bertelor","Kiraikan bulu dudok mengeram","Buah masak hampai nak gugor","Dudok menunggu siang dan malam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121046,"Burong gagak terbang melayang","Terbang ke rumah cik Soh","Oghang banyak tengok kat abang","Oghang kata abang bodoh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121047,"Burong gagak terbang melayang","Terbang pi ke rumah Cik Soh","Oghang banyak tengok kat abang","Oghang kata abang bodoh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121048,"Burong gagak terbang tinggi","Lintaih lalu puncak bukit","Ada kala baek hati","Ada kala hati sakit", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121049,"Burong gagak terbang tinggi","Sudah tinggai tak tekawai","Anak-anak tak bagi mengaji","Nanti tua baru menyesai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121050,"Burong gagak terbang tinggi","Sudah tinggi tak bekawan","Oghang banyak tak buat kenduri","Sudah lupa perintah tuhan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121051,"Burong garuda tinggi mengawan","Terbang dari pulau bangka","Maafkan hamba tuan perawan","Chinchin di-jari chinchin pusaka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121052,"Burong hantu di-pohon sena","Sa-tiap masa dia berbunyi","Satu-satu menang-nya dua","Ada kala empat pun jadi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121053,"Burong hundan turbang kalaut","Hingap kusayak pulau yang tinggi","Tuan laksana purmata jambrut","Tidak paya di pilih lagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121054,"Burong kakak tua","Bersarang rumah batu","Badan sudah tua","Gigi tinggal satu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121055,"Burong kedidi atas bumbong","Mematok berteh dengan munching-nya","Putus tali boleh ku sambong","Putus kaseh apa ubat-nya?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121056,"Burong kedidi di-tepi lombong","Mematok selaseh dengan munching-nya","Putus tali boleh di-sambong","Putus kaseh burok padah-nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121057,"Burong kedidi makan di tangan","Jinak sunggoh tegedik-gedik","Oghang dengki tak ingatkan Tuhan","Dia rasa dialah cerdik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121058,"Burong ketitir terbang menyunsang","Diikat orang di pohon jati","Albrech Durer pelukis terbilang","Memperbaiki bakat bersunggoh hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121059,"Burong kombou telornya hijau","Dudok bertelor diatas kacha","Luka tangan kerana pisau","Luka hati kerana kata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121060,"Burong kudidi terbang ka pantay","Dari pantay terbang berkawan","Kerna sa-bijik rosak sa-tangkay","Sebab sa-tangkay rosak sa-dahan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121061,"Burong kudidi trebang ka pantay","Dari pantay trebang berkawan","Krena sa bijit rosak sa tangkay","Sabab sa tangkay rosak sa-dahan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121062,"Burong lang terbang di awan","Terbang melayang di pagi hari","Terbet bintang dalam awan","Nampak deri rata negri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121063,"Burong lang terbang melayang","Tembak mari spuloh lima","Tuan sayang saya pun sayang","Handak mati mari bersama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121064,"Burong merak terbang tinggi","Sudah tinggi sampai ke awan","Soghang budak tak mau mengaji","Perangai dia macam setan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121065,"Burong merak terbang tinggi","Sudah tinggi tak berkawan","Anak dengan mak tak mau mengaji","Sebab ikut telunjok Tok Wan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121066,"Burong merbah terbang tinggi","Hinggap mari di-tepi padang","Mengidam buah pohon di-daki","Bila terchapai kenapa di-buang?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121067,"Burong merbak terlari lari","Di dahan ranting melindong dahon","Kayu nak reba tegap berdiri","Tahan di musing sedikit tahon", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121068,"Burong merpati di atas kota","Terbang menginggap di hujong balai","Api neraka ditentang nyata","Siapa tak beriman hilanglah pandai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121069,"Burong merpati di sambar lang","Bulu nya gugor di tepi sungei","Suka hati bukan kepalang","Dapat berkompol saudara ramai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121070,"Burong merpati tekenak panah","Ikan todak di atas titi","Biar mati begalang tanah","Kamek tidak memungkir janji", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121071,"Burong merpati teloqnya hijau","Ambik pecah di dalam cawan","Abang pi ke langit hijau","Adik menanti di pintu awan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121072,"Burong merpati terbang ka hutan","Inggap di tunggol bersilang kepak","Sayup nya hati pandang lautan","Bertimbol timbol di tutop ombak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121073,"Burong merpati terbang ka hutan","Inggap di tunggol bersilang kepak","Sayup nya hati pandang lautan","Bertimbol timbol di tutop ombak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121074,"Burong merpati terbang melayang","Hingga ka-senja ta' tentu hala","Kalau hati terbayang-bayang","Itu tanda chinta dah bermula", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121075,"Burong merpati terbang sa-ribu","Hinggap sa-ekor di-tengah laman","Hendak mati di-hujong kuku","Asal berkubor di-tapak tangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121076,"Burong nasar terbang ka ladang","Bulunya jatoh ka Petani","Banyak muda sudah ku Pandang","Tiada sama muda ku ini", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121077,"Burong nuri di-atas pagar","Mematok janggus dengan munchongnya","Ta' kan lari gunong di-kejar","Hilang kabus nampak-lah ia", 4, 6);
