INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121081,"Burong pekak terbang ke laot","Hendak sambaq ikan yu","Oghang kata sedap di mulot","Yang kita tidak tahu", 4, 6);
