INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121084,"Burong pekaka terbang ke laot","Hendak sambaq ikan yu","Oghang kata sedap di mulot","Kenapa dia tidak tahu", 4, 6);
