INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121101,"Burong ponggok terbang malam","Anak siapa juntay kaki","Tuju lengkong penjuru alam","Tuan sorang maskota hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121102,"Burong puchong terbang melentang","Dah melentang di bujoq pulak","Cium di hidong tanda sayang","Cium di pipi tanda segak", 4, 6);
