INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121104,"Burong punai terbang sa-kawan","Entah tidak kembali lagi","Tuan Iaksana kain kapan","Hanchor tidak berganti lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121105,"Burong puteh tekeluaq jerat","Terbang ke hutan melayang-layang","Oghang puteh sudah buat","Oghang melayu baru nak rancang", 4, 6);
