INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121107,"Burong puyoh di-atas batu","Mati terkena perangkap normah","Saya ta' gadoh memberi satu","Tapi bagaimana bunga di-rumah!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121108,"Burong puyoh terbang-nya rendah","Jatoh mati di-pulau langkawi","Pandai sunggoh abang bermadah","Di-dalam hati belum ketahui", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121109,"Burong rawa terebang tinggi","Hinggap mari di dahan sena","Teman ketawa senang dicari","Teman menangis susah bejumpa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121110,"Burong ruak-ruak dadanya merah","Terbang hinggap di kayu mati","Saya melawak janganlah marah","Itu hanya penghiboq hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121111,"Burong ruak-ruak dadanya merah","Terbang hinggap di muka pintu","Saya melawak janganlah marah","Adat kita saja dah begitu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121112,"Burong saekor atas titian","Terbang mengigap pohon rumania","Dikalu dapat payong kesaktian","Kalu di bukak tedoh dunia", 4, 6);
