INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121114,"Burong Serindek di kota padang","Singga hinggap di kuala daik","Kalu ada ringgit di pinggang","Smua orang di kata baik.", 4, 6);
