INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121117,"Burong sialu terbang menglapar","Ingap di charang ka pokok pinang","Ilang ahus ilang lapar","Kenang kan dagang merasa kenyang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121118,"Burong sintaq belari-lari","Oghang ligan rata bendang","Kawaq hendak mencuri padi","Tuan ligan lari mengengkang", 4, 6);
