INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121120,"Burong tekukoq di rantin mati","Anak harimau di perdi meranti","Dudok bekukok mencari rezeki","Jatoh ke bawah harimau pelari", 4, 6);
