INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121126,"Burong terbang di sambar lang","Membawak makan di pokok kayu","Barang di gendong jikalu ilang","Pecha rusia maklop pun tau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121127,"Burong terbang di tengah laman","Kembang kan sayap ka udara","Di junjong kenang brapa jeman","Tentang di adap berantara", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121128,"Burong terbang pulang ka-sarang","Sarang berada di-pohon kedondong","Kalau abang di-rantau orang","Jangan-lah lupa adek di-kampong!", 4, 6);
