INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121155,"Burung anggak terbang ke hutan","Hingganya mari meniti-niti","Kencang lagak janji buatan","Cucuk kaban inda berisi", 4, 6);
