INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121158,"Burung balam dua sekawan","Turun melayang di padang rata","Tengah malam terkenangkan tuan","Jatuh bercucuran si air mata", 4, 6);
