INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121160,"Burung bangau hinggap di tasik","Hinggap di tasik di Teluk Likas","Sakit angau jangan diusik","Jika diusik marahnya lekas.", 4, 6);
