INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121163,"Burung bangau terbang ke hilir","Singgah di belat berjuntai kaki","Saya umpama lanyau di air","Sebarang tertadung menjadi daki", 4, 6);
