INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121614,"Buruong marobah babunyi dokek","Di subuoh hari hondaklah ingek","Kato nobi ondaklah ingek","Ingekkan kayo sabolun bansek", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121615,"Busar gunong abis tabakar","Disana nampak api kalihatan","Laksana burong diam di sangkar","Lama tidak turbang kahutan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121616,"Busok daging tarok garam","Makan mari sayo lobak","Besar angin ribot smalam","Pulau di laut tidak ber grak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121617,"Busuk senohong jual tak laku","Ikan tenggiri memang berharga","Duduk termenung menongkat dagu","Mengenang diri ditalak tiga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121618,"Buta hidup tidak beriman","Alamat badan akan tenggelam","Bila mengikut jalan setan","Alamat masuk neraka jahanam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121619,"Buta-buta disangka mempelam","Manis mempelam masam kedondong","Penat mata bersengkang malam","Kisah silam payah dibendung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121620,"Butar-butar mataktak","Butar-butar maningkii","Mate pe amana","Adong anakna manggantii", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121621,"Butir nangka jangan dikendong","Baik kendong telur punai;","Air mata jangan diladong","Kalau diladong jadi anak sungai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121622,"Butir padi sebar di ladang","Musim panen baru dituai","Berubah tak selalu menyenangkan","Walau akhir lebih sesuai", 4, 6);
