INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121628,"Buyung dikambus si sekam padi","Sekam padi hitam dibakar","Tidak tertebus si emas budi","Hilang dicari diganti sukar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121629,"Cabai melaka bersama halia","Ditumbuk lumat tidak berkuah","Warga merdeka taat setia","Syukuri nikmat di bumi bertuah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121630,"Cabang kayu berdaun lebat","Dimana lagi buah bergantung","Orang ku rindu tak mau dekat","Kemana lagi membawa untung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121631,"Cabang kayu sudahlah runtuh","Kemana lagi lebah bersarang","Orang ku nanti sudah menjauh","Kemana lagi kasih menumpang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121632,"Cabang mana tidak bergetah","Embun mana tak turun pagi?","Bumi tak limbang buluh tak patah","Ular dibalun biarlah mati.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121633,"Cabiklah kain terkena paku","Mau dijahit tidak berbenang","Hidup ku miskin dagang piatu","Dimanakan dapat bersama abang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121634,"Cabuah dibuang uzur bapakaikan","Sasek suruik gawa maubahkan","Kok manyalang mamulangkan","Dapek sajo kalau dibarikan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121635,"Cabuk manis dua setangkai","Olehku membeli di kedai Cina","Mulutnya manis sedang kaupakai","Banyak yang sudah orang terkena", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121636,"Cabuk-cabuk bertali rambai","Sudah rambai bertali purun","Orang di tabuk kancang melambai","Kalau kasih ke mari turun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121637,"Cabuk-cabuk bertali rambai","Sudah rambai bertali purun","Orang di tabuk sedang melambai","Kalau kasih ke mari turun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121638,"Cabuklah cabuk bertali umbai","Bekasnya umbai bertali purun","Adik di tabuk sedang melambai","Kalau sudi ke mari turun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121639,"Cabut hobik di hujung jambatan","Diolah tapai bacampor ragik","Handak batakon terasa sopan","Kalukok Diang kadak ringgik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121640,"Cabut paku tanamkan tebu","Jauh rimbanya padi Jambi","Tak patut tuan begitu","Jauh ibunya hati kami.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121641,"Cabut paku tanamkan tebu","Jauh rimbanya padi Jambi","Tak patut tuan begitu","Jauh hibanya hati kami", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121642,"Cabut paku tanamkan tebu","Jauh rimbanya padi Jambi","Tak patut tuan buat begitu","Jauh hibanya hati kami", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121643,"Cabut sebatang ubi keledek","Hendak dimasak untuk berbuka","Walau ditendang badan tergolek","Badan jalak haram tak luka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121644,"Cabut ubi penuh seguni","Titik peluh hasil petani","Tubuh nan sasa gagah jasmani","Kuat semangat gagah rohani", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121645,"Cacak galah di tepi tebing","Pucuk nipah kelibatan","Sudah rosak cindai Keling","Rosak di dalam pelipatan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121646,"Cacak galah seberang sini","Elak-elakkan beri bertali","Kehendak Allah bukankah ini","Anggur relakan sekali-kali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121647,"Cacak pancang di tengah harus","Hanyut ke benting atas pasirnya","Tuan laksana benda yang halus","Sudah di tinting lahir warnanya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121648,"Cacaklah ubi tanda bertani","Ubi dikerat di dalam taman","Banyaklah nabi turun ke bumi","Nabi Muhammad nan akhir zaman", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121649,"Cacing gelang naik ke mata","Mata rabun payah membaca","Miskin ilmu miskin harta","Cermin diri mencari punca", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121650,"Cacing-cacing kepala lembut","Air pasang tenang dahulu","Sudah terpandang subang berkilat","Belum makan kenyangnya dahulu.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121651,"Cacing-cacing kepala liat","Air pasang tenang di hulu","Sudah terpandang subang berkilat","Belum makan kenyang dahulu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121652,"Cadiak handak mamakan sajo","Haram talak namuah tamakan","'Gadang jo cadiak' nan katigo","Mamintak namuah baragiah sagan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121653,"Cahaya purnama gemerlapan","Dari senja ke hujung malam","Jika itu yang sobat tanyakan","Ialah Nabi Ismail Alaihissalam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121654,"Cahaya akan redup","Matahari siang bersinar","Kita bukan mencari hidup","Tapi hidup yang wajar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121655,"Cahaya apa di tanjung tuan","Memancar-mancar bersilang-silang","Kapal berlayar mengikut haluan","Supaya tidak terlanggar karang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121656,"Cahaya apa di Tanjung Tuan","Memancar-mancar bersilang-silang","Kapal belayar mengikut haluan","Supaya tidak terlanggar karang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121657,"Cahaya berbundung jaluran murni","Suram di hutan pepohonnya banyak","Alam dibendung selamat dihuni","Jangan lautan tercemar minyak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121658,"Cahaya bersih permata berlian","Bukan setaraf batu delima","Terima kasih peminat sekalian","Maaf bermaaf kita bersama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121659,"Cahaya bintang awan bertabir","Kemilau warna bagai suasa","Pulut segantang ragi sebutir","Dari mana manis terasa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121660,"Cahaya bintang awan bertabir","Kemilau warna penaka varnis","Pulut segantang ragi sebutir","Dari mana datangnya manis?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121661,"Cahaya bintang awan bertabir","Mega kencana berbaris-baris","Pulut segantang ragi sebutir","Dari mana datangnya manis?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121662,"Cahaya bintang awan bertabir","Meja kencana disapu varnis","Pulut segantang ragi sebutir","Dari mana datangnya manis?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121663,"Cahaya bintang nampaknya indah","Sinar bercambah sambung-sambungan","Selamat datang ke negeri Kedah","Kekal muhibah jadi kenangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121664,"Cahaya bulan diliput awan","Dipuput bayu awan berlalu","Hasrat hatiku padamu tuan","Malang diriku tuan tak tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121665,"Cahaya bulan diliput awan","Dipuput bayu awan berlalu","Hasrat hatiku padamu tuan","Malang diriku tuan tak tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121666,"Cahaya hidup menyegar padi","Ayam berkokok mengikar tuah","Jikalau hidup tidak berbudi","Umpama pokok tidak berbuah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121667,"Cahaya intan nampak bersih","Di senja kala simpan di baldu","Yang ke selatan membawa kasih","Ke utara pula mengirim rindu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121668,"Cahaya malam gilang gemilang","Kerana disinar bulan purnama","Gajah mati meninggalkan tulang","Manusia mati meninggalkan nama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121669,"Cahaya mentari di celah daun","Daun gugur di makan belalang","Jasad dan akal terus membangun","Tiada diri diinjak orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121670,"Cahaya mentari silau menyilau","Digantung tabir indah menawan","Menyanyi lagu pantun riau","Semua hadirin sangat terkesan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121671,"Cahaya redup awannya kabur","Terbang helang mengirai tuah","Jikalau hidup tidak bersyukur","Pasti hilang tujuan dan arah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121672,"Cahaya redup menyegar padi","Ayam berkokok mengikat tuah","Jikalau hidup tidak berbudi","Umpama pokok tidak berbuah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121673,"Cahaya sungguh bulan mengembang","Awan juga membawa diri","Kusangka orang tetap mengenang","Awak juga tak tahu diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121674,"Cahaya terserlah ke dalam kebun","Cuaca terang sampai ke pagi","Cuba diolah ke dalam pantun","Menjadi kenangan sampai ke mati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121675,"Cahayanya lembut menembus awan","Dari senja sampailah pagi","Lidahnya lembut rupa menawan","Hatinya mulia perangai terpuji", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121676,"Cahayanya memancar berseri-seri","Dipandang takut ditentang malu","Ayer ditelan terasa duri","Tidor ta' lenyak makan ta' lalu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121677,"Cak ikak tedung","Gulai ikan pari","Berarak berusung","Nikah anak tiri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121678,"Cak kekok kaki ayam","Berlaga sama puyuh","Tak cekap berjalan malam","Ambil damar buat suluh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121679,"Cak layuk makan sireh","Buah kenang dalam padi","Hudah burok saya tak pileh","Asai bekenan di dalam hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121680,"Cak loyak memakan sireh","Buah lempani di dalam cupak","Hudah burok saya tak pileh","Ikut bekenan ibu dan bapak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121681,"Cak loyak memakan sireh","Buah lempani di dalam padi","Hudah burok saya tak pileh","Asai bekenan di dalam hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121682,"Cak pi ke hulu","Cak makan katak","Cak cari ibu","Aku cari bapak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121683,"Cakap bukan sebarang cakap","Cakap pandai nampak ilmunya","Kurap bukan sebarang kurap","Kurap bagai kelopak bunga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121684,"Cakap bukan sebarang cakap","Cakap pandai nampak ilmunya","Kurap bukan sebarang kurap","Kurap bagai kelopak bunga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121685,"Cakapan pelet orang Petani","Mari ke Tanjung hendak membeli","Bodoh kurang paham erti","Kambeli di kata permaidani", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121686,"Cakrawala kita memandang","Panorama sering bertukar","Di bawah naungan pohon rindang","Tak banyak tanaman bertumbuh segar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121687,"Cakrawala tak ada pantai","Batas pandang nampak kecil","Anak merasa aman dicintai","Orang tua sudah berhasil", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121688,"Cakua sa-sumpik sa-katidieng","Latak di dalam kibang pandan","Sadah ta-palik pado dindieng","Iko ka ganti nyao-badan.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121689,"Calak alah tajampun alun","Tibo di bawah manyimpaikan","Adat alah sarakpun alun","Tingga di awak mamakaikan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121690,"Calar-balar gunung didaki","Di senja kala letih terduduk","Bila ular akan berkaki","Kuda pula bila bertanduk?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121691,"Cambuik Ulando kok ka pacuan","Patah kamudi ditahankan","Ribuik jo pinto bakeh tuan","Adiak nan jangan dilupokan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121692,"Cambuk Belanda ada di pacuan","Patah kemudi ditahankan","Ribut permintaan pada tuan","Adik jangan dilupakan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121693,"Cameron Highlands ladangnya teh","Taman bunga di puncak bukit","Mencari ilmu segar tak letih","Amal bertambah mulanya sedikit", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121694,"Camin di rumah Malin Sutan","Urang di pakan bajua bali","Ingin di buah manggih utan","Masak ranum tagantuang tinggi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121695,"Camin di rumah Rajo Jintan","Dipacamin urang tiok pagi","Ingin ati di manggih utan","Masak ranum tagantuang tinggi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121696,"Cermin di rumah Rajo Jintan","Dipakai orang tiap pagi","Ingin hati manggis hutan","Masak ranum tergantungnya tinggi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121697,"Camin dirumah Malin Sutan","Bao ka pakan jua-beli","Ingin di buah manggih-utan","Apo dayo ta-gantuang tinggi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121698,"Campak jala batunya nyaring","Jala rambang olak belanak","Badan bulat bagai tenggiling","Laksana beronok berminyak-minyak.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121699,"Campak jala hendaklak kembang","Rebah berpusing dalam lubuk","Bukan ikan 'nak cucuk insang","Kepada saya jangan gelogok.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121700,"Campak kail umpan dibubuh","Anak nelayan mencari lokan","Seribu penawar derita tak sembuh","Penyakit hati ubatnya al-Quran", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (121701,"Campur ketumbar dengan pinang","Pinang huma atas pangkin","Sehari selembar benang","Lama-lama menjadi kain", 4, 6);
