INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122248,"Cincin permata gelang seurat","Berikan sulam merah kesumba","Keramat cinta rindu memberat","Menunggu bulan jatuh ke riba", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122249,"Cincin permata tuan sarungkan","Tanda jiwa menaruh hati","Hidup sederhana kita amalkan","Tanda takwa di dalam hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122250,"Cincin sebentuk dalam raga","Patah ditimpa tutup peti","Maka tak mau berniaga","Seperti bukan lanang sejati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122251,"Cincin sebentuk intan baiduri","Rambut lebat disanggul lintang","Umur suntuk harta dicari","Terasa lambat ajal datang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122252,"Cincin silan dari kayangan","Indah permai berwarna-warna","Kaseh puan tak ada bandingan","Sanggup bercerai badan dan nyawa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122253,"Cincin suasa batunya nilam","Baru dibeli sarung ke jari","Itik angsa lemas di kolam","Ayam di kepuk padi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122254,"Cincin suasa buatan Melaka","Hendak borong di pekan Semenyih","Masa berkuasa bermuka cuka","Dah habis siung termonyeh-monyeh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122255,"Cincin suasa buatan Melaka","Hendak di borong di pekan Semenyih","Masa berkuasa bermuka cuka","Dah habis suing termonyeh-monyeh.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122256,"Cincin suasa buatan Melaka","Hendak diborong di pekan Semenyih","Masa berkuasa bermuka cuka","Dah habis siung termonyeh-monyeh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122257,"Cincin suasa di jari dara","Penghias diri pergi ke pekan","Sayangi bangsa cintai negara","Tanggungjawab diri kita tunaikan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122258,"Cincin suasa intan gemala","Emas sebungkal dilebur dilakar","Baru merasa manisnya gula","Sudah ditinggal halwa bersakar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122259,"Cincin suasana batu delima","Sumbi emas menampal gigi","Nikmat dirasa sedikit Cuma","Di syurga nanti lebihlah lagi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122260,"Cindai bersulam di kaki lima","Buat pengalas santapan puteri","Di hujung malam kita bersama","Dengar dendangan irama asli", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122261,"Cindai kuning emas bertekat","Dipakai putera Seri Langkat","Barang siapa meninggalkan zakat","Tidaklah hartanya beroleh berkat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122262,"Cindai sutera dari Semarang","Laksemana berkuda unta","Jangan ketara di tengah terang","Laksana orang bersilap mata", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122263,"Cing kerincing","Kerlat bunga lada","Temu hantu kucing","Ajak bersilat dengan Pak Da", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122264,"Cing kerincing","Setinggi bunga lada","Mana hantu kucing","Nasi basi atas para", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122265,"Cino barajuik kapalonyo","Ulando pulang ka nagari","Kok tak tasangkuik dianggono","Bara nio tuan kami bali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122266,"Cina berajut kepalanya","Belanda pulang ke negeri","Jika tak tersangkut kerana harga","Berapa mau tuan kami beli", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122267,"Cinta dunio balalai-lalai","Kandaraannyo duto tak amanah","Pakaian dunia haram nan bapakai","Palitonyo lupo kapado Allah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122268,"Cinto-cinto di dalam parak","Inggiran balam panjang ikua","Kalau tacinto bao galak","Sabuik namonyo bao tidua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122269,"Cinta-cinta di dalam parak","Tempat bertengger balam panjang ekor","Kalau tercinta bawalah gelak","Sebut namanya bawalah tidur", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122270,"Cipire mrambat kawat","Godhong bendha kentir kali","Gak mampir nek ketok liwat","Wis lega rasane ati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122271,"Buah kecipir merambat-rambat","Daun bendha hanyut di kali","Biar tak singgah asalkan lewat","Aku puas legalah hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122272,"Ciup api embun bederai","Patah galah haluan perahu","Hajat di hati tak mahu becerai","Kuasa Allah siapa yang tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122273,"Coba kita mengilir taji","Taji diilir buluh tamiang","Coba kita bersindir nyanyi","Tuan seorang saya seorang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122274,"Coban buluh lidah patah","Sirat jala tiada menjadi;","Dua tiga gunung ku langkah","Tiada sama adindaku tadi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122275,"Cok gelecok","Gali-gali ubi","Di mana kayu bongkok","Di situ musang menjadi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122276,"Cok laut ka'ai kisa","Dak udang balik daun","Dari e'et tanggung esa","Baik e'et bujang sampai bertahun", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122277,"Cok laut terik kisa","Cop miku udang galah","Biar e'et nanggung esa","Kajet enyot serba salah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122278,"Cok laut terik pukat","Kop miku ka gamak","Main nohok sama sepakat","Kalau kokup main samak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122279,"Cok teluk ketam tipak","Haga gulai pucuk ngala","Cok bakau keret tercampak","Telog untung cop wala", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122280,"Cok-cok gali cok","Ambil cok gali ubi","Mana kayu bongkok","Tempat kera meniti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122281,"Colok dipotong dengan sekin","Sekin waja buatan Jawa;","Hendaklah diturut dengan yakin","Yakin di hati selamatlah nyawa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122282,"Colok dipotong dengan sekin","Sekin waja buatan Jawa;","Hendaklah diturut dengan yakin","Yakin di hati selamatlah nyawa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122283,"Colok ditaruh ke dalarn cerakin","Cerakin jati buatan Jawa","Hendaklah kita menaruh yakin","Yakin di hati selamatlah nyawa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122284,"Condong akan rebah bunga mengkudu","Bunga sitawar disusuni","Tolong dan sembah oleh penghulu","Gawa saya harap ampuni.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122285,"Condong ke barat pokok kemboja","Bunganya lebat jangan dilurut","Badannya berat malas kerja","Tangan dipangku cerutlah perut!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122286,"Condong pokok kelapa ditebang","Tempat belatuk membuat sarang","Padi hampa bak debu terbang","Janji kosong kubiar usang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122287,"Condongkan rebah pohon mengkudu","Bunga setawar disusunkan","Tolong dan sembah oleh penghulu","Gawa saya harap maafkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122288,"Cong mak incong","Adik kakak","Mulut muncung","Mata tegak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122289,"Congek cat dong","Cat dong duku dakat","Apa nenek kendong","Kendong butir langsat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122290,"Congek-congek canggung","Canggung duku daka","Apa nenek kendong","Kendong butir nangka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122291,"Congek-congek canggung","Canggung kayu nangka","Apa nenek kendong","Kendong kain sahaja", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122292,"Congek-congek canggung","Canggung labi laba","Buat bangsal makyung","Nak main apa?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122293,"Congek-congek canggung","Canggung labi laba","Buat bangsal makyung","Nak main bila?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122294,"Congek-congek canggung","Canggung labi laba","Buat bangsal makyung","Nak main malam apa?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122295,"Conggang-conggong kerabat batang","Batang tumbang kayu melata","Kering cengkong mata buntang","Terlunjuk kediri tikam mata.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122296,"Contoh jas dari Selipi","Boleh ditaruh dalam kereta","Tiap malam mendapat mimpi","Itulah tanda sangat bercinta.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122297,"Contoh terbilang dalam Quran","Firaun menindas Bani Israel","Acuan terbaik acuan Tuhan","Ingkar bererti golongan jahil", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122298,"Contoh utama Doktor Hamka","Menulis terjemahan Quran dalam penjara","Dari keturunan baik belaka","Kesusahan diri tak diambil kira", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122299,"Corak anyaman itulah kelarai","Kelarai bernama sib eras patah","Sunyi teman baru bercerai","Bagai nangka tidak bergetah!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122300,"Corak bergigi si pucuk rebung","Dilukis dilorek kepala kain","Bagai kerbau dicucuk hidung","Setuju saja usah berlain!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122301,"Corak corek si bunga batik","Cocok awan dengan raginya","Bapanya borek anaknya rintik","Betapa acuan begitulah kuihnya.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122302,"Corak dam berkotak-kotak","Kain tenun dari Kelantan","Lidah tergigit pingan pun retak","Manis hidup sedikit kepahitan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122303,"Corak ditempa kain disulam","Kain kasa memang jarang","Hampas kelapa dimakan ayam","Hampas kata dibenci orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122304,"Cuaca baik jalannya baik","Sedikit becak di Kampung Tambalang","Hendak naik biarlah naik","Jangan terpijak kepala orang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122305,"Cuaca baik seluruh kampung","Anak raja turun ke taman","Burung baik sendiri yang tanggung","Tepuk dada tanyalah iman", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122306,"Cuaca cerah menjadi redup","Masakan boleh kembali terang","Budi bahasa kembali hidup","Barulah kekal dihormati orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122307,"Cuaca gelap semakin redup","Masakan boleh kembali terang","Budi bahasa amalan hidup","Barulah kekal dihormati orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122308,"Cuaca gelap semakin redup","Masakan boleh kembali terang","Budi bahasa amalan hidup","Barulah kekal dihormati orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122309,"Cuaca gemilang nun membiru","Malam purnama gemerlap bintang","Bila menjelang tahun baru","Azam lama digilap ditatang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122310,"Cuaca hari menjelang terang","Awan pepejal kepulan berlari","Semasa diri dijulang orang","Jangan khayal lupakan diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122311,"Cuaca kelam berlayar jauh","Asap berkepul hanyut terapung","Besi tenggelam itulah sauh","Belon timbul namanya pelampung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122312,"Cuaca kering panas memijar","Jemur kain penuh balai","Usah ditanya usah dikejar","Kash orang payah dinilai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122313,"Cuaca redup bulan tak terang","Jalannya lurus ke Timbang Menggaris","Kita hidup bukan seorang","Ada penerus dan ada pewaris.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122314,"Cuaca redup di hari libur","Tiga puluh hari bulan puasa","Semasa hidup jangan takbur","Ada lagi yang lebih berkuasa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122315,"Cuaca redup di kota Melaka","Pedagang berlayar menggunakan tongkang","Kita hidup di Negara merdeka","Tidak berilmu tinggal di belakang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122316,"Cuaca redup hendak ke Kangar","Ikut ke Bukit Besi Hangit","Seumur hidup tak pernah dengar","Kononnya hujan naik ke langit", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122317,"Cuaca redup tidak membasuh","Gadis Tatana lantas ke kota","Jika hidup sering bermusuh","Tidak ke mana jadinya kita.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122318,"Cuaca teduh lindung-lindungan","Leka berkelah di Seri Mersing","Jangan menuduh sembarangan","Menangkah kalah sebagai samseng", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122319,"Cuaca terang pergi berkelah","Ketawa berdekah bersama rakan","Ramai orang membeli belah","Cik Jamil samakah sesakkan pekan?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122320,"Cuaca terang sampai ke pagi","Orang ke surau beransur pulang","Menjadi kenangan sampai ke mati","Agar ia tak akan berulang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122321,"Cuba bertanam asam paya","Tapi tumbuh belimbing besi","Jikalau tuan tidak percaya","Belahlah dada tengoklah isi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122322,"Cuba-cuba bertanam mumbang","Moga-moga tumbuh kelapa","Cuba-cuba bertanam sayang","Moga-moga menjadi cinta", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122323,"Cuba-cuba bertanam sirih","Kalau ada bertumbuh suli;","Cuba-cuba bertambat kasih","Moga ada hendak unjuk bagi.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122324,"Cuba-cuba menyabung ayam","Sudah suka menjadi judi","Cuba-cuba menabur sayang","Sudah suka jumpa tok kadi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122325,"Cuba-cubalah tanam selasih","Moga tidak tumbuh kepayang","Cuba-cubalah mananam kasih","Moga-moga tumbuhlah sayang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122326,"Cubadak ambiak kagulai","Talatak di tangah padang","Apo nan titah di pegawai","Ditiliek dalam undang-undang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122327,"Cubadak di tangah padang","Buahnyo ambiak ka gulai","Apa nan cupak di dubalang","Baruliah titah dari pagawai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122328,"Cubadak di tangah padang","Buahnyo ambiak ka gulai","Apo nan cupak di dubalang","Baruliah titah dari pagawai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122329,"Cubadak duo satali","Gandang nan duo salabuhan","Siriah galak pinang manari","Sadah marantak di kapuran", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122330,"Cempedak dua setali","Gendang dua selabuhan","Sirih galak pinang menari","Sedah merentak di kapuran", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122331,"Cubadak duo ta-gantuang","Sa-buah pang-gulai maco","Baru ta-kana pada untuang","Tidua ba-tilam aia-mato.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122332,"Cubadak tanam daulu","Tanam dibaliak jawi jawi","Kok taragak tahan daulu","Mananti sabulan sapuluah ari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122333,"Cempedak tanam dahulu","Tanam di balik jawi jawi","Kalau rindu tahanlah dahulu","Menanti sebulan sepuluh hari", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122334,"Cubadak tangah halaman","Tataruang dek ampu kaki","Jan lamo tagak di halaman","Itu cibuak basuahlah kaki", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122335,"Cubadak tumbuah sabatang","Tumbuah luruih dakek parigi","Nan rancak diliek urang","Budi haluih mamikek hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122336,"Cuci batu kain basahan","Hilanglah debu dan kotoran","Kalaulah keputusan dengan perasaan","Bagaimana mau menegakkan kebenaran?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122337,"Cuci piring cuci tangan","Cuci juga muka dan badan","Jalani baik baik perkawinan","Di depan mungkin ada perceraian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122338,"Cuci-cuci gigi","Kumoq ayaq carok","Macam mana cuci","Itu ayaq kotoq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122339,"Cuci-cuci kain","Gabuih kain burok","Soghang sakit hati","Soghang sakit tengkok", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122340,"Cuci-cuci kaki","Gabuih kain burok","Sapa kata cuci","Kaki kotoq terok", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122341,"Cuci-cuci kaki","Tapi nampak kotoq","Aku bukan benci","Aku Cuma tegoq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122342,"Cucu dudok di rumah","Tok Wan pi kerja","Cucu berasa megah","Hidup macam raja", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122343,"Cucu udang gatong","Cicit udang galah","Kain batek sentong","Baju kebaya belah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122344,"Cucuk nipah berduri nipah","Orang Palembang pergi ke Jambi","Duduk susah berdiri susah","Menahan bimbang di dalam hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122345,"Cucuk sanggul intan bertatah","Sanggul lintang bunga mayang","Menanti nangka tidak bergetah","Menunggu rebung hilang miang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122346,"Cucuk surapit","Surapit daun limau","Siapa tapihit","Ditangkap oleh harimau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122347,"Cucur badak keledek dilecek","Masukkan inti digoreng pula","Anak muda kemas bersolek","Tahu tujuan tahu hala", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122348,"Cucur cucur babi beruan","Koyak nanas menual kulit","Saya tak suko demo ambik tunang kawan","Tak lama kawan nak ambil balik", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122349,"Cuit-cuit pandai","Elalang jungkit-jungkit","Ecuit dengan pandai","Aagak dengan kuungkit-ungkit", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122350,"Bercuit-cuit pandai","Belalang jungkit jungkit","Bercuit engkau pandai","Nanti engkau kujangkit-jangkit", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122351,"Cuka di dulang Datuk Menteri","Orang menyuluh cerana papan;","Sukalah rakyat di dalam negeri","Kapal disuruh ke Teluk Ketapang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122352,"Cuka-cuka beli di bandar","Simpan makan dalam perahu","Suka-suka mahu belajar","Tidak belajar mana tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122353,"Cukin datang dari China","Pasir putih di sarang helang","Miskin sudah dagang yang hina","Hitam putih dikata orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122354,"Cukin datang dari China","Pasir putih di sarang helang","Miskin sudah dagang yang hina","Hitam putih dikata orang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122355,"Cukup bekal sampai ke petang","Pulut inti sambal sotong","Anak tunggal manja ditatang","Buah hati pengarang jantung!", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122356,"Cukup liat jangan disimpan","Musuh orang tak bergigi","Kalau mayat sudah dikapan","Kenapa pula menggali perigi?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122357,"Cukup ramai orang di kampung","Tengok orang berjual kain","Sungguh garang ayam di kampung","Denak di hutan terkamnya lain", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122358,"Cukup sudah memasang api","Tetapi hari tak juga terang","Cukup sudah abang menanti","Tetapi adik tak juga datang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122359,"Cukup sudah orang beramu","Hingga petang barulah sudah","Hidupku susah dagang piatu","Dimanakan abang mau menikah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122360,"Cukup sudah orang berburu","Tetapi belum mendapat rusa","Cukup sudah abang menunggu","Tetapi adik tak acuh juga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122361,"Cukup sudah orang berlayar","Tetapi kolek tak mau laju","Cukup sudah abang bersabar","Tetapi adik tak mau tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122362,"Cun cuin kelentong ayan","Pakai tali benang nanas","Untung sekumin rugi sekoyan","Bagaimana hati tak panas", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122363,"Cupu emas rantainya perak","Permainan Siti Zaleha","Dunia ini sajakan parak","Negeri akhirat andang sedia", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122364,"Curah pinang berlambak-lambak","Mari lambak dalam lancok","Air tenang ikan berolak","Orang di pantai bersorak tepuk", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122365,"Cut Nati rajo di Aceh","Disangko anak rajo Judah","Mukasuik ati alah lapeh","Sangketo mato alun lah sudah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122366,"Dadahan cacah bergula","Bakul-bakul beri bertali","Inda tahan hati kaola","Baik ditakul sama sekali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122367,"Dadahan cecah bergula","Bakul-bakul beri bertali","Tiada tertahan hati kaula","Baik ditakul sama sekali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122368,"Dadak tingga ayam mangakeh","Dipiliah atah ciek-ciek","Padi nan alah jadi bareh","Lalu bakameh dibaok naiak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122369,"Daerah Keramat di Kota Paluh","Berpuput bayu ke gunung batu","Sembah hormat jari sepuluh","Sembah Melayu kenapa begitu?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122370,"Daerah Penampang tempat rumahnya","Di Jalan Pintas Kudat-Sipitang","Adat kumbang mencari bunga","Tidak pantas melarang pantang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122371,"Dagang luar kain kelubung","Ganti selendang menutup kepala","Hidup bersarna selantai sebumbung","Satu tujuan satu hala", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122372,"Dagang luar kain sarung","Kain disauk bawa ke tepi","Kurang guni ku bawa karung","Rezeki melimpah bukanya mimpi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122373,"Daging ayam masak berkuah","Buat bekalan ke Kuala Linggi","Hati mengidam nak makan buah","Buah masak di dahan tinggi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122374,"Daging liat disiat-siat","Nangka sebelah bawa berkelah","Jangan melihat kecilnya maksiat","Renunglah keagungan Allah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122375,"Daging liat rebus sepetang","Api marak masih di tungku","Perkara maksiat wajib ditentang","Islam tidak memilih bulu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122376,"Daging punai dalam dulang","Kasi makan Datuk Temenggung","Jangan bawa resmi udang","Ekor berdetik tahi dijunjung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122377,"Daging rusa dilayat-layat","Dicampur susu lemak gulainya","Janji setia ke akhir hayat","Anak cucu saksi buktinya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122378,"Daging sapi di sayat sayat","Susu diminum terasa sehat","Kalau ingin di hormati rakyat","Cukup yang tua jadi penasehat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122379,"Daging sapi dimasak rendang","Masak nasi dalam kukusan","Laki-laki kalau di ranjang","Lupa dunia hilang ingatan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122380,"Dago dagi mambari malu","Sumbang salah laku parangai","Jalankan nan bana dek panghulu","Pantangkan kusuik tak salasai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122381,"Daguah dagah punai padi","Dibao anak rang mudiak Padang","Kaluah kasah manahan ati","Di bao lalok manilantang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122382,"Desah mendesah punai padi","Di bawa anak orang mudik Padang","Keluh kesah menahan hati","Di bawa tidur menelentang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122383,"Dah berlari ke dalam taman","Terjun ke air lalu berenang","Tiku Benua dengan Pariaman","Persembahkan tanda sirih peminang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122384,"Dah tahu kami akan baju","Saat mencelup berkederang","Sudah tahu kami akan laku","Selagi menghadap", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122385,"Dah tahu kami dibaju","Dalam batalok bakudarang","Dah tahu kami di laku","Dalam maadok mambalakang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122386,"Dahan kamunieng bialah patah","Asa mangkudu jan talendo","Di lahie rajo nan disambah","Di bathin rakyaik nan kuaso", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122387,"Dahan kandis ditimpa mengkudu","Tempat pekaka hinggap menanti","Wajah manis senyum bermadu","Hati terluka siapa mengerti", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122388,"Dahan kapas dahan berangan","Daun pandan disusun rapat","Joran telepas di tapak tangan","Ikan dikail tidakku dapat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122389,"Dahan kayu biarkan mati","Esok lusa boleh dijaja","Irama Melayu satukan hati","Warisan bangsa sama dijaga", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122390,"Dahan kuini dibuat para","Batang lalang mengatap bangsal","Hidup ini umpama pengembara","Akan pulang ke tempat asal", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122391,"Dahan layu tunggu berpucuk","Pokok pandan di tepi perigi","Sudah dirayu sudah dipujuk","Nasib badan orang tak sudi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122392,"Dahan luruh telanjang ranting","Ranting repuh jatuh ke tanah","Wajah keruh tanda runsing","Tetamu dijemput hilang ramah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122393,"Dahan melampai ke belukar","Ditanam oleh raja yang sakti","Bunga dililit ular yang besar","Sangatlah gundah di dalam hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122394,"Dahan melampaui ke belukar","Ditanam oleh raja yang sakti","Adikku duduk di tempat sukar","Sangatlah gundah di dalam hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122395,"Dahan melempai ke belukar","Ditanam oleh raja yang sakti","Adikku duduk tempat yang sukar","Sangatlah gundah di dalam hati", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122396,"Dahan mempelam terhayun-hayun","Ditiup angin dari selatan","Jemu berdiam bertahun-tahun","Ku karang pantun jadi ingatan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122397,"Dahan patah kayu berduri","Kepayang tumbuh dalam dulang","Dagangan indah tidak terbeli","Sayang sungguh nyawa akan hilang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122398,"Dahan pauh dahan mempelam","Dipungut daun ambil buahnya","Hari rusuh bercampur dendam","Takut", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122399,"Dahan rekah rantingnya patah","Elok juga dapat kukerat","Ke laut sudah ke gunung sudah","Ke bulan juga ingin kudarat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122400,"Dahan rindang tempat bernaung","Rendah sarang burung tempuah","Terang memandang dari gunung","Daripada di dalam gua", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122401,"Dahan terpaut hangus dek panas","Dahan meranti menimpa tupai","Gelombang di laut ombak mengganas","Gelombang di hati hajat tak sampai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122402,"Dahanla ada unting puan lada","Mari digigit dengan tangkainya","Pesan puan ada pembuni puan ada","Yang mana juga warna begini", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122403,"Dahanlah ada unting puan lada","Mari digigit dengan tangkainya","Pesan puan ada pembuni puan ada","Yang mana juga warna begini", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122404,"Dahlia merah penyeri meja","Meja hiasan tuan puteri","Masa lapang rebutlah segera","Zaman muda cuma sekali", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122405,"Dahon trap dahon kunyit","Dahon pandan di-timba ruang","Harap hujan dari langit","Ayer tempayan di-balek buang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122406,"Dahulu berambut panjang","Kini berambut hingga bahu","Dahulu berkasih sayang","Kini seperti tidak tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122407,"Dahulu berambut panjang","Kini berambut hingga bahu","Dahulu berkasih sayang","Kini serupa tidak tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122408,"Dahulu parang sekarang besi","Besi keramat encik hang tuah","Dahulu sayang sekarang benci","Sejak mendapat gading bertuah.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122409,"Dahulu rabab nan batangkai","Kini lah kopi nan babungo","Dahulu adat nan bapakai","Kini lah rodi nan paguno", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122410,"Dahulu rebab yang bertangkai","kini kopi yang berbunga","Keluhan","Keluhan kerana tidak tahan menanggung derita;  Dahulu menggunakan adat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122411,"Dahulu rebab yang bertangkai","Kini kopi yang berbunga","Dahulu adat yang terpakai","Kini rodi yang berguna", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122412,"Dahulu rebab yang bertangkai","Kini kopi yang berbunga","Dahulu adat yang terpakai","Kini rodi yang berguna", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122413,"Dai Dumba ke Kampung Tekek","Dari sana ke Tanjung Kelapa","Terdapat juga tiga buah tasik","Semuanya terkenal belaka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122414,"Dai Labuan pergi Muara","Sampai Muara membeli kelapa","Jasa tuan untok Negara","Sampai mati kami tak lupa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122415,"Dakaq side bedoe paoq","Endeq rebang lalon kedondang","Dakaq side uah taoq","Endeq rebang lalon sombong", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122416,"Dalaem sumur pete karung sede","Sumur sino endeqna beraiq","Lamun tiang tebelin isiq side","Mangan nginem endeqna maiq", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122417,"Dalam 10 tahun yang sudah berlalu","Infrastruktur dimantap dengan jitu","Pakar dipilih yang nombor satu","Kakitangan dilatih agar profesional selalu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122418,"Dalam aie dapek di ajuak","Dalam hati siapo tahu","Sungguah kawek nan dibantuak","Ikan di aie nan dituju", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122419,"Dalam Al Qur'an pun di kisahkan","Manusia pertama Adam di ciptakan","Hidup di surga nikmat dihadiahkan","Kasih sayang Allah di cucurkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122420,"Dalam Al Qur'an pun di kisahkan","Manusia pertama Adam di ciptakan","Hidup di surga nikmat dihadiahkan","Kasih sayang Allah di cucurkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122421,"Dalam alam ada bunyi","Dalam bunyi ada bayang","Dalam bayang ada bicara","Dalam bicara ada makna", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122422,"Dalam arakan bawa bendera","Jalan bergegas baris melebar","Kami doakan selamat sejahtera","Dengan tugas lebih mencabar", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122423,"Dalam Bahasa Melayu 'baca'","Dengan membaca bertambah ilmu","Di hati mereka terpancar cahaya","Memajukan negara tanpa jemu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122424,"Dalam bahtera ke Tanjung Pinang","Ubi keladi longgok di tepi","Tumpang gembira semasa senang","Namun budi elok bertampi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122425,"Dalam bakiu mengkuang","Isi barang-barang","Kalau oghang tuang","Kita boleh tendang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122426,"Dalam baldi si buah rumia","Rumia masak sedap dimakan","Sudah menjadi resam dunia","Barang yang susah kita idamkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122427,"Dalam belanga ikan pari","Buah halia patah sekerat","Harta dunia kita cari","Untuk bahagia dunia akhirat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122428,"Dalam belanga santan kelapa","Buat kolak pisang berangan","Niat hati mengikat keluarga","Jika bapak dan ibu izinkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (122429,"Dalam berbual pelihara cakap","Dalam berjalan berhemat cermat","Dalam beramal pelihara adab","Dalam beriman ingat makrifat", 4, 6);
