INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (123602,"Dato' Onn nama diberi","Pejuang Melayu jasa gemilang","Adat hidup terima dan beri","Akan menjadi kenang-kenangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (123603,"Dato Paduka menyerang lanun","Menyerang lanun dekat Kedurong","Tidak sia-sia hajat di pohon","Sakalian ra'ayat rasa beruntong", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (123604,"Dato Pertinggi hendak ka seberang","Hilir sa-kejap ka kedai China","Sunggoh pun tinggi burong terbang","Akhir hingga juga dia-nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (123605,"Dato Pertinggi hendak ka seberang","Hilir sa-kejap ka-kedai China","Sunggoh pun tinggi burong terbang","Akhir hinggap juga dia-nya", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (123606,"Dato Sardon jadi menteri","Cukup terator di dalam negeri","Bertahun-tahun abang menanti","Menunggu gugor pauh baiduri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (123607,"Dato' Sulaiman SUP Sabah","Orangnya tenang bijak berfikir","Walaupun aman kita muhasabah","Agar senang menerima takdir.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (123608,"Datok bicara pagi petang","Boleh tahu kalah menang","Kalau dengaq kata oghang","Boleh kalah dengan pengarang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (123609,"Datok Dollah panglima Rajah","Jalan berampat masok di-kota","Sedang debelah sedang di-jagah","Kalu tak dapat nasib chelaka", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (123610,"Datok Kelana orang Linggi","Bersipak raga ditepi kota","Kita-tak boleh kadarat sandiri","Hukoman Allah terima-la kita", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (123611,"Datok pengulu bukak keday","Tempat semayang tepi kolam","Hujan di hulu hujan di hilay","Gunong yang tinggi hampir nak karam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (123612,"Datok raja rebah tergolek","Dalam istana bagi nak gila","Dudok sahja tiada bersolek","Sabagi bungan bahru mala", 4, 6);
