INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (123615,"Datuak Majoindo rang si Tujuah","Jadi pakuih di Si Junjuang","Arok saya dangan ba-sungguah","Pantun nago ma-lingka gunuang.", 4, 6);
