INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (123618,"Datuk Bendahara bertajak kemas","Bersama panglima tersenyum manis","Sebagai pesara berusia emas","Mari bersama hidup harmonis", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (123619,"Datuk Bendahara mengambil senjata","Memanah ungka di pohon beringin","Apa bicara memujuk wanita","Perangainya suka naik angin?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (123620,"Datuk Bentara nampaknya gagah","Memegang gada panah berdamak","Apa daya untuk mencegah","Pemuda-pemudi berambut semak?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (123621,"Datuk Bentara terima kurnia","Perdana Menteri pangkat utama","Jauh kembara keliling dunia","Bermula dari langkah pertama", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (123622,"Datuk Ghani menunggang kuda","Kuda dari Pantai Melingsung","Hidup ini amat menggoda","Tahankan diri iman tak kosong.", 4, 6);
