INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (123640,"Datuk tiba berkalang","Minyak dalam piring","Bila datuk berjalan","Banyak orang iring", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (123641,"Dau oghang bini","Dok sana-sini","Laki maki-maki","Ikut oghang pi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (123642,"Daulat nan dipartuan di Pagaruyuang","Rajo alam itu namonyo","Panghasilan panghulu pulo disambuang","Tabagi ampek parakaro", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (123643,"Daulat Tuanku setelah berdiri","Adilnya limpah sehari-hari","Raja Bendahara memangku negeri","Ikutan segala hulubalang negeri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (123644,"Daulu barambuik panjang","Kini barambuik singgan bau","Daulu bakasiah sayang","Kini bak cando tidak tau", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (123645,"Daulu namo si Centeng","Makan upah jo tukang-tukang","Daulu awak nan geneng","Kini badan mangko ta-buang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (123646,"Daulu pakuih di Lolo","Kini mantari di Si Junjuang","Tidak untuang apo kan dayo","Jan di sasa pado untuang.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (123647,"Daulu rabab nan ba-tangkai","Kini lah kopi nan ba-buah","Daulu adat nan ba-pakai","Kinilah rodi nan ba-guna.", 4, 6);
