INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (123649,"Daulu-nyo pakuih di Jambak","Kini turuik Tuan Panasa","Nan ka tuju sudah lah tampak","Iko sabab tidak ma-nyasa.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (123650,"Daum mempelas di dalam padi","Rumah merpati bertingkat-tingkat;","Amatlah belas hatiku ini","Dipangkal hati sudah terikat.", 4, 6);
