INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124171,"Di atas tilam di dalam kelambu","Bersuka-sukaan tiada terperi","Habislah kasih di dalam kalbu","Seperti kejatuhan bulan matahari", 4, 6);
