INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124176,"Di awan biru burung sekawan","Terbang rendah sayap dikembang","Bertemu di UKM para cendiakawan","Membicara pantun bukan bersembang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124177,"Di balai orang baurung","Badundang badiang dangan","Sepeninggalan Marhum di Tanjung","Mahkota lama bertukar tangan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124178,"Di balik jendela kaca","Pameran barang mutakhir","Semakin banyak membaca","Semakin pandai jangan kawatir", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124179,"Di balik pagar berikat rotan","Ikatan berselang dengan tali","Si dia terlantar tak lalu makan","Akhirnya pulang menghadap Ilahi", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124180,"Di balik pintu terkunci","Gantungan kawat berantai","Lebih mudah membenci","Daripada mencintai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124181,"Di banda tahankan tangguak","Dapeklah udang jo tali-tali","Jokok panghulu tupai tuo","Tampan lai lompek lah tingga", 4, 6);
