INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124191,"Di bawah Tuanku Titah saurang kadi","Nan bapangkat imam adat","Duduak samo randah tagak samo tinggi","Jo Datuak nan Salapan samo darajat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124192,"Di Bohor Goncar beli delima","Buah mempelam gugur ke tepi","Gunung terbakar bertahun lama","Baru semalam ternampak api", 4, 6);
