INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124201,"Di bulan Ramadhan orang tadarus","Membaca Qur'an beramai-ramai","Orang beriman hatinya lurus","Duduk berjalan elok perangai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124202,"Di bulan Ramadhan orang tarawih","Sudah sembahyang membaca Qur'an","Orang beriman hidupnya salih","Dadanya lapang lakunya sopan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124203,"Di bumi mencari jejak","Dibawa raga sampai ke tanjung","Di sini bumi dipijak","Di sini juga langit dijunjung", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124204,"Di Burmah Road menjual tebu","Tebu dibawa dari Perai","Besar benar jasanya ibu","Susah payah tidak bercerai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124205,"Di Bursa Saham Kuala Lumpur","Tempat pelabur mencari keuntungan","Globalisasi menjadi alat penggempur","Boleh disekat dengan iman", 4, 6);
