INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124209,"Di dalam guni ada tepong","Nama tepong tepong gandum","Oghang bagi dari Tanjong","Tepong asli beras gandum", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124210,"Di dalam kelas guru mengajar","Murid mendengar rapi sekali","Setiap hari Nabi beristigrfar","Paling sedikit tujuh puluh kali", 4, 6);
