INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124214,"Di dalam nyanyian budak","Terkandung budi dan syarat","Bila kecil sudah dipinak","Sesudah besar tuahnya nampak", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124215,"Di dalam peti di Pantai Katung","Di situlah mutiara abang sembunyikan","Di antara hati dengan jantung","Di situlah adik abang tinggalkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124216,"Di dalam puan disimpan sagu","Sagu menjamu ayam tambatan","Hari Keputeraan hari ditunggu","Semoga sunahmu menjadi ikutan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124217,"Di dalam raga kerang dan kepah","Ulam pegaga dibuat laksa","Kepada keluarga kasih tertumpah","Moga bahagia sepanjang masa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124218,"Di dalam saku isi bertih","Bawa ke huma untuk berjamu","Dijual tak laku gadai tak boleh","Itulah harta bernama ilmu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124219,"Di dalam semak ada kebun","Di dalam kebun ada bunga","Di dalam gelak ada pantun","Dalam pantun ada makna", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124220,"Di dalam taman Banjaran Seri","Tempat bidadari yang ketujuh","Air diminum serasa duri","Menjadi sakit sepanjang tubuh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124221,"Di dalam tangguk buah kuini","Mari dibawa dalam perahu","Jangan ditenguk badan begini","Belum dicuba belum tahu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124222,"Di dinihari bersampan kolek","Kolek teruna dari Andalas","Waspadakah nuri melihat racik","Racik yang mana kerap terbingkas?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124223,"Di dokong anak silang kasa","Di sambar lolong dalam kolam","Intan terleta gunong blaksa","Chahya melempa sa-putar alam", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124224,"Di galak gong ranchak rabana","Selagi di kota bangun menari","Antara blom jadi bichana","Biar kita ber-ingat kan diri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124225,"Di gandaria Olongia berdiri","Pedang di kanan tombak di kiri","Keris di pinggang dengan balingkung","Dola Jaksa di muka pintu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124226,"Di gantungan ada pakaian","Berganti sepulang kerja","Jangan memuji berlebihan","Anak gampang menjadi manja", 4, 6);
