INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124497,"Di tepi laut banyak karang","Di tepis ombak samudra","Kesulitan besar masa sekarang","Sesuaikan dengan zaman berubah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124498,"Di tepi laut batu kelikir","Di celah batu bertumbuh pandan","Sambil membaca sambil berfikir","Mana yang baik buat tauladan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124499,"Di tepi laut menanam kelapa","Buah salak buah keranji","Biar hidup miskin dan papa","Asalkan jangan mungkir janji", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124500,"Di tepi pagar pokok kuini","Buahnya gugur ke dalam paya","Kalau kutahu nasib begini","Apa guna hidup di dunia?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124501,"Di tepi pantai daerah Aceh","Ombak besar menyerang pantai","Kesan beriman dan beramal soleh","Baru syaitan dikenalkan rantai", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124502,"Di tepi pantai negeri Bali","Pasirnya putih bagai mutiara","Haram berniat sekali-kali","Adinda penyudah kasih dan mesra", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124503,"Di tepi perdu pohonan ciku","Budak-budak bersama rakan","Disangka syahdu dalam doaku","Kiranya tidak dikau aminkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124504,"Di tepi perdu pohonnya ciku","Bermain budak bersama rakan","Ku sangka syahdu dalam doaku","Sayang tidak dikau aminkan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124505,"Di tepi perigi bermain lotong","Anak kera di pinggir kolam","Kalau gigi hanya sepuntung","Kenapa selera nak makan ulam?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124506,"Di tepi sungai jala di tebar","Dirumah bumbu baru ditumis","Agar terbina tabah dan sabar","Cobalah puasa senin dan kamis", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124507,"Di tepi sungai jala di tebar","Jala ditebar si abang kumis","Agar terbina tabah dan sabar","Cobalah puasa senin dan kamis", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124508,"Di tepi tebat burung merpati","Terbang berdua pulang ke sarang","Kenapa bersahabat tidak sehati?","Karam berdua basah seorang?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124509,"Di teratak tua terjual tembikai","Pulang ke Segama kami singgahi","Soal kedua pakai tak pakai","Soal utama hadiah dihargai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124510,"Di Terus Madi orang mendaki","Menyikut tekiding beramai-ramai","Menabur budi sangat dikehendaki","Tuding-menuding penghalang damai.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124511,"Di Tigo Batua datuak batigo","Datuak Rajo Malano nan partamo","Datuak Simarajo nan kaduo","Datuak Rajo Panghulu nan katigo", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124512,"Di timpa hujan dalam perahu","Rakit tumbuh pandan seruni","Sakitnya bujang siapakan tahu","Sakitlah sungguh badan ku ini", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124513,"Di timur kita berkiblat","Di sini terbit matahari","Jangan memilih sahabat","Tidak serupa diri sendiri", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124514,"Di Timur Barat Utara dan Selatan","Majoriti manusia minat hiburan","Berduyun-duyun ke perangkap syaitan","Lupa kehidupan Hari Kemudian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124515,"Di tiop angin si bulang baling","Di pohon kayu kat Bukit Piatu","Serta nak pandang si nyonya pun kening","Dudok risau ati tak tentu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124516,"Di tiup angin bunga benalu","Jatuh guguran tepi telaga","Bercakap Melayu kononnya malu","Tapi belacan di hentam juga.", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124517,"Di tutop perigi ayer nya minom","Takot penchana rachun di buboh","Pokok tinggi bua pun ranom","Jatoh di tana menganchor loloh", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124518,"Di ujung jalan buntu","Harapan bisa hilang","Orang mau berlaku dungu","Karena suatu pendirian", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124519,"Di urang mulia kalau nak tahu","Like kapado kalakuannyo","Di urang baalemu kalau nak tahu","Indak nyo muak baraja salalu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124520,"Di zaman Tun Sri Lanang","Penulis Buku Sejarah Melayu","Jasa ibubapa tetap dikenang","Memang disuruh ajaran wahyu", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124521,"Dia juga muda yang janda","Mangka di sebut tuan kekanda","Sana kemari dia bercanda","Sobatnya banyak tuan olanda", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124522,"Dia membuat sebilah pedang","Disimpan gula dengan sirih","Sudah ada Nabi Adam","Ada pula Nabi Idris", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124523,"Dia nangkatmu?","Dia ultopmu?","Dia hatamu?","Dia nidokmu?", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124524,"Diadap oleh anak bendahara","la bernama Siti Maimunah","Dengan orang jangan ketara","Karena sahaya belum pernah", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124525,"Diajarkan kepada rakyat Melayu","Melalui Bahasa Melayu kebanggaan kita","Tempat remaja Melayu berpoya-poya","Terkejut bila JAWI  bertindak segera", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124526,"Diajuk panggil burung tekukur","Terkena panah ratu pelimbang","Rumah kecil bumbungnya bocor","Tangga patah lantai berlubang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124527,"Dialah pemilik Bejana Emas","Tuangkan air dengan penuh cinta","Menyambung rahmat tanpa ampas","Bagi qalbi yang tengah menderita", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124528,"Dialah Tuhan Maha Pemurah","Tiada berbalas selain Kasih Sayang","Jika engkau menyelami esensi surah","Nama dan sifat lain kan tertuang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124529,"Diam di geta bilik bersekat","Sekat bertebuk pecah sembilan","Bagai si buta kehilangan tongkat","Laksana si cebol mencapai bulan", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124530,"Diam di taman Indera Kayangan","Menggubah bunga kuntum rajasa","Kepada kakanda jadi kenangan","Tempawan jadi penambat rasa", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124531,"Diam itu hulu bicaro","Tanang itu saribu aka","Kalau nak tahu tungku nan tigo","Kuek baguru rajin baraja", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124532,"Diam melangkah lewat titian","Dua mata bertemu pandang","Lepaskan pendirian","Kekuasaan pasti hilang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124533,"Diam ubi diam berisi","Diam besi diam berkarat","Bersyukur di kala mendapat rezeki","Biar sedikit bawa berkat", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124534,"Diambiak kini dimakan kapatang","Tiok salah ado hukunnyo","Bia salah ketek jo gadang","Hukumnyo alah tasadio", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124535,"Diambiak misa ka pajabaik","Atau pagawai di nagari","Karano basalah diturunkan pangkaik","Puntuang diambiak pananak nasi", 4, 6);
