INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124537,"Diambil oleh peranak kaling","Ditenunkan kain panjang lima","Air mata tiadakan kering","Masakah mana pulang bersama", 4, 6);
