INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124539,"Di-ambil sa-dahan di-buat sogok","Sogok di-haluan sa-buah bahtera","Pohon-lah patek ayer sa-tegok","Di-harapkan rahim serta mesra", 4, 6);
