INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124541,"Diam-diam naik ke sampan","Membawa jala beserta jaring","Dalam diam adik doakan","Semoga kanda tidak berpaling", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124542,"Diam-diam naik perahu","Membawa barang untuk berdagang","Dalam diam adik menunggu","Semoga abang lekas meminang", 4, 6);
INSERT INTO pantun (id, bayang1, bayang2, maksud1, maksud2, jenis, sumber) VALUES (124543,"Diam-diam orang beramu","Membawa badik untuk senjata","Dalam diam abang menunggu","Semoga adik mau menyapa", 4, 6);
